/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {
	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;

	tit_clearActionInfo();
	tit_addSearchActionInfo("wb02:WB0202006A_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}
/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{
	if(ds_list.rowcount == 0)
	{
		gfn_showMsg("CW00113",st_param.value);
		return;
	}

	if ( !gfn_isChangeDs(ds_list, true, ""))
	{
		return;
	}

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""
		,"wb02:WB0202006A_U1"
		,""
		);
	tit_callService(
        ""
        , ""
        , "ds_list=ds_list:A"
        , ""
        , ""
        , "fn_afterSave");		
}
/********************************************************************************
* 기      능   : 전송 - ZHRT001에서 ZTBESAJU1으로 데이타 마이그레이션
********************************************************************************/
function fn_trans(){
	if (gfn_isNull(cal_gdate,true, st_gdate.Value,cal_gdate)) return false;
	if (gfn_isNull(med_amt1,true, st_amt1.Value,med_amt1)) return false;
	if (gfn_isNull(med_amt2,true, st_amt2.Value,med_amt2)) return false;
	if (gfn_isNull(med_amt3,true, st_amt3.Value,med_amt3)) return false;

	if (!gfn_showConfirm("CI00010")) {
		return;
	}

	tit_clearActionInfo();
	tit_addSingleActionInfo("wb02:WB0202006A_I1");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, ""
		, ""
		, "fn_afterTrans"
		, true);
}
/********************************************************************************
* 기      능   : 전송2 - ZTBESAJU1의 한도금액 재계산 update
********************************************************************************/
function fn_trans2(){
	tit_clearActionInfo();
	tit_addSingleActionInfo("wb02:WB0202006A_U2");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, ""
		, ""
		, "fn_afterTrans2"
		, true);
}

