/********************************************************************************
* 기      능   : print 40
********************************************************************************/
function fn_print() {

	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_list, ds_list_0, "CHECK=1");

	if (ds_list_0.rowcount < 1)
	{
		gfn_showAlert("CW00013");
		return;
	}

	// UbiReport Call
	var arg = " fa_page=" + quote(cb_print.Value);

	Dialog("WB02::WB0202006B.xml", arg);	
}



