/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		gfn_selectGridCol(obj);
	} else 	if ( nCell == 2 ) {
		//  전체 선택 해제
		ds_list.fireevent = false;
		gfn_selectAllGrid(obj,2,2);
		ds_list.fireevent = true;
	} else {
		gfn_sortGrid(obj,nCell);
	}
}
/********************************************************************************
* 기      능   : 사원검색 팝업
********************************************************************************/
function btn_psno_OnClick(obj)
{
	var psno_arr = gfn_openPopup("WB0301001B",true,null);
	if (psno_arr != null)
	{
		if (obj.id == "btn_psno")
		{
			ds_cond.SetColumn(0,"PSNO",substr(psno_arr[0],1));
		}
		else
		{
			ed_psno.value = psno_arr[0];
			ed_psno_OnKillFocus();
		}
	}	
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;

	if(length(trim(ds_list.GetColumn(nRow,"SIN_AMT"))) == 0) ds_list.SetColumn(nRow,"SIN_AMT",0);
	if(length(trim(ds_list.GetColumn(nRow,"CHU_AMT"))) == 0) ds_list.SetColumn(nRow,"CHU_AMT",0);
	if(length(trim(ds_list.GetColumn(nRow,"DAE_AMT"))) == 0) ds_list.SetColumn(nRow,"DAE_AMT",0);

	var hanAmt = ParseInt(ds_list.GetColumn(nRow,"HAN_AMT"));
	var sinAmt = ParseInt(ds_list.GetColumn(nRow,"SIN_AMT"));
	var sumAmt = ParseInt(ds_list.GetColumn(nRow,"SIN_AMT"))+ParseInt(ds_list.GetColumn(nRow,"CHU_AMT"));
	var daeAmt = ParseInt(ds_list.GetColumn(nRow,"DAE_AMT"));
	switch( strColumnID ) {
		case "SIN_AMT":
			if(hanAmt < sinAmt)
			{
				//gfn_showAlert("한도내(I) 신청금액은 한도금액보다 클수없습니다.\n확인하여 주십시오.");
				var arr = [st_sin.value,st_han.value];
				gfn_showAlert("CW00190",arr);

				ds_list.SetColumn(nRow,"SIN_AMT",ds_list.GetOrgColumn(nRow,"SIN_AMT"));
				ds_list.SetColumn(nRow,"SUM_AMT",ParseInt(ds_list.GetColumn(nRow,"SIN_AMT"))+ParseInt(ds_list.GetColumn(nRow,"CHU_AMT")));
				break;
			}
			else if(sumAmt < daeAmt)
			{
				//gfn_showAlert("융자신청금액은 한도내(I)+한도외(II)신청금액보다 클수없습니다.\n확인하여주십시오.");
				var arr = [st_dae.value,st_sum.value];
				gfn_showAlert("CW00190",arr);
				ds_list.SetColumn(nRow,"DAE_AMT",ds_list.GetOrgColumn(nRow,"DAE_AMT"));
				ds_list.SetColumn(nRow,"SUM_AMT",sumAmt);
				break;
			}
			else
			{
				ds_list.SetColumn(nRow,"SUM_AMT",sumAmt);
				break;
			}
		case "CHU_AMT":
			//if(hanAmt < sumAmt)
			if(false)
			{
				//gfn_showAlert("한도내(I)+한도외(II)신청금액은 한도금액보다 클수없습니다.\n확인하여 주십시오.");
				var arr = [st_sum.value,st_han.value];
				gfn_showAlert("CW00190",arr);
				ds_list.SetColumn(nRow,"CHU_AMT",ds_list.GetOrgColumn(nRow,"CHU_AMT"));
				ds_list.SetColumn(nRow,"SUM_AMT",ParseInt(ds_list.GetColumn(nRow,"SIN_AMT"))+ParseInt(ds_list.GetColumn(nRow,"CHU_AMT")));
				break;
			}
			else if(sumAmt < daeAmt)
			{
				//gfn_showAlert("융자신청금액은 한도내(I)+한도외(II)신청금액보다 클수없습니다.\n확인하여주십시오.");
				var arr = [st_dae.value,st_sum.value];
				gfn_showAlert("CW00190",arr);
				ds_list.SetColumn(nRow,"DAE_AMT",ds_list.GetOrgColumn(nRow,"DAE_AMT"));
				ds_list.SetColumn(nRow,"SUM_AMT",sumAmt);
				break;
			}
			else
			{
				ds_list.SetColumn(nRow,"SUM_AMT",sumAmt);
				break;
			}
		case "DAE_AMT":
			//if(sumAmt < daeAmt)
			if(false)
			{
				//gfn_showAlert("융자신청금액은 한도금액보다 클수없습니다.\n확인하여 주십시오.");
				var arr = [st_dae.value,st_sum.value];
				gfn_showAlert("CW00190",arr);
				ds_list.SetColumn(nRow,"DAE_AMT",ds_list.GetOrgColumn(nRow,"DAE_AMT"));
				break;
			}
			else if(sumAmt < daeAmt)
			{
				//gfn_showAlert("융자신청금액은 한도내(I)+한도외(II)신청금액보다 클수없습니다.\n확인하여주십시오.");
				var arr = [st_dae.value,st_sum.value];
				gfn_showAlert("CW00190",arr);
				ds_list.SetColumn(nRow,"DAE_AMT",ds_list.GetOrgColumn(nRow,"DAE_AMT"));
				break;
			}
	}	
	obj.fireevent = true;
}
