}



/********************************************************************************
* 기      능   : 복원 
********************************************************************************/
function fn_copy() {
    var oldRow = ds_list.rowpos;
	ds_list.AddRow();
	ds_list.CopyToRow(oldRow,ds_list,ds_list.currow);
	ds_list.SetColumn(ds_list.rowpos,"FLAG","I");
	ds_list.SetColumn(ds_list.currow,"PCAPNO","");
	ds_list.SetColumn(ds_list.currow,"DPT1","");
	ds_list.SetColumn(ds_list.currow,"DPT1_N","");
	ds_list.SetColumn(ds_list.currow,"RANK","");
	ds_list.SetColumn(ds_list.currow,"RANK_N","");
	ds_list.SetColumn(ds_list.currow,"IRUM","");
	ds_list.SetColumn(ds_list.currow,"PCBPME","");
	ds_list.SetColumn(ds_list.currow,"PCAMAC","");
	ds_list.SetColumn(ds_list.currow,"PCAPCS","");

	ed_pno.Enable = true;
	ed_pno.SetFocus();
}


/********************************************************************************
* 기      능   : 복원 
********************************************************************************/
function fn_reset() {
	gfn_initForm();
	ds_list.Reset();
}
