		//ed_pcc.Enable = false;
		//cb_pmo.Enable = false;
		//ed_phd.Enable = false;
		//cb_pcr.Enable = false;
		//ed_pvg.Enable = false;
		//ed_pip.Enable = false;
		cb_pgn.Enable = false;
		//cb_pcl.Enable = false;
		me_mac.Enable = false;
	}
	else if (cb_gbn.Value == "N")
	{
		//cb_pcl.Enable = false;
		cb_pgn.Enable = false;		
	}
	else if (cb_gbn.Value == "T")
	{
		//cb_pcm.Enable = false;
