function grd_list_OnCellDBClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var arg = " fa_pno=" + quote(obj.GetCellValue(nRow,9));

	gfn_openWin("WB0301004A", arg);

}

function grd_list_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	if (ds_list.GetColumn(nRow,"FLAG") == "I")
	{
		ed_pno.Enable = true;
	}
	else
	{
		ed_pno.Enable = false;
	}
}

function cb_oaall_OnClick(obj,strValue)
{
	if (cb_oaall.Value == "1") {
		cb_dpt1.Index = "0";
		cb_dpt1.Enable = false;
	}
	else if (cb_oaall.Value != "1") {
		cb_dpt1.Enable = true;
	}
}
