function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

    // 엑셀 버튼 비활성화
    ufn_enableBtn("excel", false);

	tit_clearActionInfo();
	tit_addSearchActionInfo("wb03:WB0301001A_S2");

	tit_callService(
        ""
        , ""
        , ""
        , "ds_dpt1_cd=ds_dpt1_cd"
        , ""
        , "fn_afterInit"
        , true);

    if (isExistVar("fa_pno", true))
    {
		ds_cond.SetColumn(0,"PCCPNO", fa_pno);
		fn_query();
    }
    else
    {
	   // 완료메세지
	   gfn_showMsg("CI00007");
    }
}
