/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	tit_clearActionInfo();
	tit_addSearchActionInfo("wb03:WB0301004A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 삽입 
********************************************************************************/
function fn_input() 
{
	ds_list.AddRow();
	ds_list.SetColumn(ds_list.rowpos,"FLAG","I");
	cal_pdt.Enable = true;
	if (ds_list.RowCount() > 1) 
	{
		ds_list.SetColumn(ds_list.rowpos,"PCAGBN",ds_list.GetColumn((ds_list.rowpos-1),"PCAGBN"));
		ds_list.SetColumn(ds_list.rowpos,"PCCPNO",ds_cond.GetColumn(0,"PCCPNO"));
		ds_list.SetColumn(ds_list.rowpos,"DPT1_N",ds_list.GetColumn((ds_list.rowpos-1),"DPT1_N"));
		ds_list.SetColumn(ds_list.rowpos,"IRUM",ds_list.GetColumn((ds_list.rowpos-1),"IRUM"));
		ds_list.SetColumn(ds_list.rowpos,"PCCPME",ds_list.GetColumn((ds_list.rowpos-1),"PCCPME"));
		ds_list.SetColumn(ds_list.rowpos,"PCCPGN","U");
		ds_list.SetColumn(ds_list.rowpos,"PCCPDT", gfn_getCurrDate());
		ds_list.SetColumn(ds_list.rowpos,"PCBPNO",ds_cond.GetColumn(0,"PCCPNO"));
		ds_list.SetColumn(ds_list.rowpos,"PCBPME",ds_list.GetColumn((ds_list.rowpos-1),"PCBPME"));
		ds_list.SetColumn(ds_list.rowpos,"PCAPPD",ds_list.GetColumn((ds_list.rowpos-1),"PCAPPD"));
		ds_list.SetColumn(ds_list.rowpos,"PCAPXD",ds_list.GetColumn((ds_list.rowpos-1),"PCAPXD"));

		ed_ber.SetFocus();
	}
	else
	{
		ds_list.SetColumn(ds_list.rowpos,"PCCPNO",ds_cond.GetColumn(0,"PCCPNO"));
		ds_list.SetColumn(ds_list.rowpos,"PCCPGN","N");
		ds_list.SetColumn(ds_list.rowpos,"PCAPDT", gfn_getCurrDate());
	}
}
/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {
	if ( ds_list.rowCount() == 0 ) {
		// 삭제할 자료 선택 
		// CW00013=처리할 자료를 먼저 선택해 주십시오.
		gfn_showMsg("CW00013");
		return;
	}

	if (ds_list.GetColumn(ds_list.currow,"FLAG") == "I")
	{
		ds_list.DeleteRow(ds_list.currow);
	}
	else
	{
		ds_list.setColumn(ds_list.rowpos, "FLAG", "D");
	}


//	gfn_sortGrid(ds_list,"REQSN");
}
/********************************************************************************
* 기      능   : 청구 전송
********************************************************************************/
function fn_save() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

    // 자료가 없으면 리턴
	if (ds_list.rowcount < 1){
		gfn_showAlert("CW00010");
		return;
	}
    //변경값 확인
	if ( !gfn_isChangeDs(ds_list, true) ) {
		return;
	}

	ds_list.fireevent = false;
	for (var i=0;i < ds_list.rowcount; i++)
	{
		// 장비번호 체크
		if ( length(ds_list.GetColumn(i,"PCCPDT")) < 8) {
			gfn_showMsg("CW00001","변경일자");
			return;
		}

		if ( ds_list.GetColumn(i,"FLAG") == "I" || ds_list.GetColumn(i,"FLAG") == "U")
		{
			switch(ds_list.GetColumn(i,"PCCPGN"))
			{
				case "U" : ds_list.SetColumn(i,"PCAPPD",ds_list.GetColumn(i,"PCCPDT"));
						   ds_list_0.AddRow();
						   ds_list_0.CopyRow(ds_list_0.rowpos,"ds_list",i);
						   ds_list_0.SetColumn(ds_list_0.rowpos,"FLAG", "I");
						   break;
				case "D" : ds_list.SetColumn(i,"PCAPXD",ds_list.GetColumn(i,"PCCPDT"));
						   ds_list_1.AddRow();
						   ds_list_1.CopyRow(ds_list_1.rowpos,"ds_list",i);
						   ds_list_1.SetColumn(ds_list_1.rowpos,"FLAG", "I");
						   break;
				case "C" : ds_list_2.AddRow();
						   ds_list_2.CopyRow(ds_list_2.rowpos,"ds_list",i);
						   ds_list_2.SetColumn(ds_list_2.rowpos,"FLAG", "U");
						   break;
				default : break;
			}
		}
	}
	ds_list.fireevent = true;

//tit_showDs(ds_list,false);
	if (!gfn_showConfirm("CI00010")) {
		return;
	}


	tit_clearActionInfo();
	tit_addSaveActionInfo(
			"wb03:WB0301004A_I1"
			, "wb03:WB0301004A_U1"
			, "wb03:WB0301004A_D1"
			, "FLAG"
			,""
			, 0
			, "");

	tit_addSaveActionInfo(
			"wb03:WB0301004A_U2"
			, ""
			, ""
			, "FLAG"
			,""
			, 0
			, "");

	tit_addSaveActionInfo(
			"wb03:WB0301004A_U3"
			, ""
			, ""
			, "FLAG"
			,""
			, 0
			, "");

	tit_addSaveActionInfo(
			""
			, "wb03:WB0301004A_U4"
			, ""
			, "FLAG"
			,""
			, 0
			, "");
	tit_callService(
		""
		, ""
		, "ds_list=ds_list:A ds_list_0=ds_list_0:A ds_list_1=ds_list_1:A ds_list_2=ds_list_2:A"
		, ""
		, ""
		, "fn_afterSave");
}

/********************************************************************************
* 기      능   : 거래명세서 전송취소 
********************************************************************************/
function fn_cancel() {
	ds_list.Reset();
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {
	gfn_exportExcel(grd_list,G_USER_ID);
}

/********************************************************************************
* 기      능   : 복원 
********************************************************************************/
function fn_reset() {
	gfn_initForm();
	ds_list.Reset();
}
