/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}


/********************************************************************************
* 기      능   : 장비번호검색 팝업
********************************************************************************/
function btn_pno_OnClick(obj)
{
	var pno_arr = gfn_openPopup("WB0301004B",true,null);
	if (pno_arr != null)
	{
		ds_cond.SetColumn(0,"PCCPNO",pno_arr[0]);
		fn_query();
	}
}


/********************************************************************************
* 기      능   : 데이터 변경시 FLAG
********************************************************************************/

function fn_onColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (obj.GetColumn(nRow,"FLAG") == "")
	{
		obj.SetColumn(nRow, "FLAG", "U");
	}
}


/********************************************************************************
* 기      능   : 사원검색 팝업
********************************************************************************/
function btn_pme_OnClick(obj)
{
	var pme_arr = gfn_openPopup("WB0301001B",true,null);
	if (pme_arr != null)
	{
		ds_list.SetColumn(ds_list.rowpos,"PCCPME",pme_arr[0]);
		ds_list.SetColumn(ds_list.rowpos,"IRUM",pme_arr[1]);
		ds_list.SetColumn(ds_list.rowpos,"DPT1_N",pme_arr[3]);
		ds_list.SetColumn(ds_list.rowpos,"PCBPME",pme_arr[0]);
		ds_list.SetColumn(ds_list.rowpos,"PCCPGN","C");
	}	
}

function ufn_onCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	if(ds_list.GetColumn(nRow,"FLAG") == "I")
	{
		cal_pdt.Enable = true;
	}
	else
	{
		cal_pdt.Enable = false;
	}
}
