function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

    // 엑셀 버튼 비활성화      
    ufn_enableBtn("excel", false);
    ufn_enableBtn("copy", false);

	tit_clearActionInfo();
	tit_addSearchActionInfo("wb03:WB0301001A_S2");	// 부서정보조회

	tit_callService( 
        ""
        , ""
        , ""
        , "ds_dpt1_cd=ds_dpt1_cd"
        , ""
        , "fn_afterInit"
        , true);

   // 완료메세지
   gfn_showMsg("CI00007");


}
/********************************************************************************
* 기      능   : 부서조회 후 콜백
