/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";
	// 사용자
	if (length(ed_pcbpme.value) > 0 && substr(ed_pcbpme.Value,0,1) != "H")
	{
		ds_cond.SetColumn(0,"ACC_USER","H"+ed_pcbpme.Value);
	}	

	tit_clearActionInfo();
	tit_addSearchActionInfo("wb03:WB0301006D_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 삽입 
********************************************************************************/
function fn_input() 
{
	ds_list.AddRow();
	// currow
	ds_list.SetColumn(ds_list.rowpos,"FLAG","I");
	ds_list.SetColumn(ds_list.rowpos,"PART_NO","");
	ds_list.SetColumn(ds_list.rowpos,"PART_NM","");
	ds_list.SetColumn(ds_list.rowpos,"PART_MC","");
	ds_list.SetColumn(ds_list.rowpos,"PART_COST","");
	ds_list.SetColumn(ds_list.rowpos,"PART_GB","");
	ds_list.SetColumn(ds_list.rowpos,"PART_TYPE","");
	ds_list.SetColumn(ds_list.rowpos,"BILL_DATE","");
	ds_list.SetColumn(ds_list.rowpos,"ACC_DATE","");
	ds_list.SetColumn(ds_list.rowpos,"ACC_USER","");
	ds_list.SetColumn(ds_list.rowpos,"ACC_USER_NM","");
	ds_list.SetColumn(ds_list.rowpos,"ACC_DPT","");
	ds_list.SetColumn(ds_list.rowpos,"ACC_DPT_NM","");
	ds_list.SetColumn(ds_list.rowpos,"ACC_CNT","");
	ds_list.SetColumn(ds_list.rowpos,"TOTAL_COST","");
	ds_list.SetColumn(ds_list.rowpos,"PARTNER","");
	ds_list.SetColumn(ds_list.rowpos,"PRO_YN","");
	ds_list.SetColumn(ds_list.rowpos,"STA_YN","");
	ds_list.SetColumn(ds_list.rowpos,"REMARK","");

}
/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {
	if ( ds_list.rowCount() == 0 ) {
		// 삭제할 자료 선택 
		// CW00013=처리할 자료를 먼저 선택해 주십시오.
		gfn_showMsg("CW00013");
		return;
	}

	if (ds_list.GetColumn(ds_list.currow,"FLAG") == "I")
	{
		ds_list.DeleteRow(ds_list.currow);
	}
	else
	{
		ds_list.setColumn(ds_list.rowpos, "FLAG", "D");
	}


//	gfn_sortGrid(ds_list,"REQSN");
}
/********************************************************************************
* 기      능   : 청구 전송
********************************************************************************/
function fn_save() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

    // 자료가 없으면 리턴
	if (ds_list.rowcount < 1){
		gfn_showAlert("CW00010");
		return;
	}
    //변경값 확인
	if ( !gfn_isChangeDs(ds_list, true) ) {
		return;
	}

	ds_pcc.ClearData();
	for (var i=0;i < ds_list.rowcount; i++)
	{
		if (ds_list.GetColumn(i,"FLAG") != "" )
		{
			// 제품번호 체크
			//if ( length(ds_list.GetColumn(i,"PART_NO")) < 1) {
			//	gfn_showMsg("CW00001","제품번호");
			//	return;
			//}
			// 제품명
			if ( length(ds_list.GetColumn(i,"PART_NM")) < 1) {
				gfn_showMsg("CW00001","제품명");
				return;
			}

		/*	ds_pcc.AddRow();
			ds_pcc.SetColumn(ds_pcc.rowpos ,"FLAG", "I");
			if (ds_list.GetColumn(i,"FLAG") == "I")
			{
				ds_pcc.SetColumn(ds_pcc.rowpos ,"PCCPDT", ds_list.GetColumn(i,"PCAPID"));
				ds_pcc.SetColumn(ds_pcc.rowpos ,"PCCPGN", "N");
			}
			else
			{
				ds_pcc.SetColumn(ds_pcc.rowpos ,"PCCPDT", gfn_getCurrDate());
			}
			ds_pcc.SetColumn(ds_pcc.rowpos ,"PCCPNO", ds_list.GetColumn(i,"PCAPNO"));
			ds_pcc.SetColumn(ds_pcc.rowpos ,"PCCPME", ds_list.GetColumn(i,"PCBPME"));


			// 변경내역에 따라 구분값 선정
			if (ds_list.GetColumn(i,"FLAG") == "U" 
			       && ds_list.GetColumn(i,"PCBPME") != ds_list.GetOrgColumn(i,"PCBPME"))
			{
				ds_pcc.SetColumn(ds_pcc.rowpos ,"PCCPGN", "C");
			}

			if (ds_list.GetColumn(i,"PCAPPD") != ds_list.GetOrgColumn(i,"PCAPPD"))
			{
				if (length(ds_list.GetColumn(i,"PCAPPD")) > 0)
				{
					ds_pcc.SetColumn(ds_pcc.rowpos ,"PCCPGN", "U");
					ds_pcc.SetColumn(ds_pcc.rowpos ,"PCCPDT", ds_list.GetColumn(i,"PCAPPD"));
				}
			}
			if (ds_list.GetColumn(i,"PCAPXD") != ds_list.GetOrgColumn(i,"PCAPXD"))
			{
				if (length(ds_list.GetColumn(i,"PCAPXD")) > 0)
				{
					ds_pcc.SetColumn(ds_pcc.rowpos ,"PCCPGN", "D");
					ds_pcc.SetColumn(ds_pcc.rowpos ,"PCCPDT", ds_list.GetColumn(i,"PCAPXD"));
				}
			}

			//  삭제시 PCC
			if (ds_list.GetColumn(i,"FLAG") == "D")
			{
				ds_pcc.SetColumn(ds_pcc.rowpos ,"PCCPGN", "E");
				ds_pcc.SetColumn(ds_pcc.rowpos ,"FLAG", "");
			}

			if (length(ds_pcc.GetColumn(ds_pcc.rowpos,"PCCPGN")) < 1)
			{
				ds_pcc.DeleteRow(ds_pcc.rowpos);
			}
			*/
		}

	}
//tit_showDs(ds_pcc,false);

	if (!gfn_showConfirm("CI00010")) {
		return;
	}

	tit_clearActionInfo();
	tit_addSaveActionInfo(
			"wb03:WB0301006D_I1"
			, "wb03:WB0301006D_U1"
			, "wb03:WB0301006D_D1"
			, "FLAG"
			,""
			, 0
			, "");
			/*
	if (ds_pcc.rowcount > 0)
	{
		tit_addSaveActionInfo(
				"wb03:WB0301001A_I2"
				, "wb03:WB0301001A_U2"
				, "wb03:WB0301001A_D2"
				, "FLAG"
				,""
				, 0
				, "");
		tit_addSaveActionInfo(
				"wb03:WB0301001A_I3"
				, ""
				, ""
				, "FLAG"
				,""
				, 0
				, "");
	}
	*/
	tit_callService(
		""
		, ""
		, "ds_list=ds_list:A ds_pcc=ds_pcc:A"
		, ""
		, ""
		, "fn_afterSave");
}

