}



/********************************************************************************
* 기      능   : 복원 
********************************************************************************/
function fn_copy() {
    var oldRow = ds_list.rowpos;
	ds_list.AddRow();
	ds_list.CopyToRow(oldRow,ds_list,ds_list.currow);
	ds_list.SetColumn(ds_list.rowpos,"FLAG","I");
	ds_list.SetColumn(ds_list.currow,"PART_NO","");
	ds_list.SetColumn(ds_list.currow,"PART_NM","");
	ds_list.SetColumn(ds_list.currow,"PART_MC","");
	ds_list.SetColumn(ds_list.currow,"PART_COST","");
	ds_list.SetColumn(ds_list.currow,"PART_GB","");
	ds_list.SetColumn(ds_list.currow,"PART_TYPE","");
	ds_list.SetColumn(ds_list.currow,"PARTNER","");

}


/********************************************************************************
* 기      능   : 복원 
********************************************************************************/
function fn_reset() {
	gfn_initForm();
	ds_list.Reset();
}
