/********************************************************************************
* 기      능   : 사원검색 팝업
********************************************************************************/

function btn_pme_OnClick(obj)
{
	var psno_arr = gfn_openPopup("WB0301001B",true,null);

	if (psno_arr != null)
	{
		if (obj.id == "btn_pme")
		{
			ds_list.SetColumn(ds_list.rowpos,"ACC_USER",psno_arr[0]);
			ds_list.SetColumn(ds_list.rowpos,"ACC_USER_NM", psno_arr[1]);
			ds_list.SetColumn(ds_list.rowpos,"ACC_DPT", psno_arr[2]);
			ds_list.SetColumn(ds_list.rowpos,"ACC_DPT_NM", psno_arr[3]);
		}
		else if(obj.id == "btn_pcbpme")
		{
			ds_cond.SetColumn(0, "ACC_USER", psno_arr[0]);
			ds_cond.SetColumn(0, "ACC_DPT", psno_arr[2]);
		}
		else
		{
			ed_pme.value = psno_arr[0];
			ed_pme_OnKillFocus();
		}
	}
}

// 사원 자료 검색

function ed_pme_OnKillFocus(obj)
{
	if (substr(ed_pme.value,0,1) != "H")
	{
		ed_pme.value = "H"+ed_pme.value;
	}
	if (length(ed_pme.value) == 8)
	{		
		ds_psno_cond.setColumn(0, "PSNO", toUpper(ed_pme.value));
		ds_psno_cond.SetColumn(0,"INPUTROW", ds_list.currow);

		tit_clearActionInfo();
		// 저장 처리 
		tit_addSearchActionInfo("wb03:WB0301001A_S3");

		tit_callService(
			""
			,""
			, "ds_psno_cond=ds_psno_cond"
			, "ds_psno_list=ds_psno_list"
			, ""
			, "fn_setPsno"
			, false
			, true);	
	}
}


/********************************************************************************
* 기      능   : 소모품 검색 팝업
********************************************************************************/
function btn_oa_search_OnClick(obj)
{
	var part_no = gfn_openPopup("WB0301006C",true,null);
	if (part_no != null)
	{
		if (obj.id == "btn_oa_search")
		{
			ds_list.SetColumn(ds_list.rowpos,"PART_NO",part_no[0]);
			ds_list.SetColumn(ds_list.rowpos,"PART_NM",part_no[1]);
			ds_list.SetColumn(ds_list.rowpos,"PART_MC",part_no[2]);
			ds_list.SetColumn(ds_list.rowpos,"PART_GB",part_no[3]);
			ds_list.SetColumn(ds_list.rowpos,"PARTNER",part_no[4]);
			ds_list.SetColumn(ds_list.rowpos,"PART_COST",part_no[5]);
		}
		else
		{
			ed_pme.value = psno_no[0];
			ed_pme_OnKillFocus();
		}
	}
}

function cal_acc_date_OnChanged(obj,strOldText,strNewText)
{
	ds_list.SetColumn(ds_list.rowpos,"ACC_DATE", obj.value);
}



function cal_bill_date_OnChanged(obj,strOldText,strNewText)
{
	ds_list.SetColumn(ds_list.rowpos,"BILL_DATE", obj.value);
}

function ed_part_cnt_OnChanged(obj,strText)
{
	ds_list.SetColumn(ds_list.rowpos,"TOTAL_COST",ToInteger(ds_list.GetColumn(ds_list.rowpos,"PART_COST"))*ToInteger(ds_list.GetColumn(ds_list.rowpos,"ACC_CNT")));
}

function btn_icon_bp_OnClick(obj)
{
	if(ds_list.rowcount > 0)
	{
		for(var i = 0 ; i <= ds_list.rowcount ; i++){
			if(ds_list.GetColumn(i, "CHECK")== 1){
				ds_list.SetColumn(i, "STA_YN", cb_sta_yn.Value);
				ds_list.SetColumn(i, "PRO_YN", cb_pro_yn.Value);
				ds_list.SetColumn(i, "BILL_DATE", cal_bill_date.Value);
				}
		}
	}

}
