function fn_input() {
	// 그리드에 row 추가 
	var cnt = ds_list.GetRowCount();

	ds_list.AddRow();
	ds_list.SetColumn(ds_list.row, "FLAG", "I");
	ds_list.RowPos = cnt;
	//ds_list.SetColumn(ds_list.row,"UDATE",G_CURR_DATE);

}
/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {

	if ( !gfn_showConfirm("CI00010") ) {
		return;
	}

	gfn_copyDs(ds_list, ds_list3, "CHECK=1");

	if(ds_list3.rowcount < 1) {

		gfn_showAlert("CW00013");
		return;
	}

	var user_id = G_USER_ID;

	if ( user_id == '2023750' || user_id == '2019841' || user_id == '2018250' ||
	     user_id == '2010119' || user_id == '2015340' || user_id == '2025205' ||
	     user_id == '2023709' || user_id == '2025817') {

	}
	else {
		for (var i = 0; i < ds_list3.rowcount; i++)
		{ 
			if(ds_list3.GetColumn(i,"UNAME") != user_id){
				gfn_showAlert("CW00072");
				ds_list3.SetColumn(i,"CHECK","0");
				return;
			}
		}
	}

	tit_clearActionInfo();
	tit_addMultiActionInfo("wb04:WB0401001A_D1");

	tit_callService(
		""
		, ""
		, "ds_list3=ds_list3"
		, ""
		, ""
		, "fn_afterDelete"
		, true);
}

/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	//Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.Value = "";

	ds_gubun5.ClearData();

	// 변경값 확인
	if ( !gfn_isChangeDs(ds_list, true) ) {
		return;
	}

	//자료가 없으면 리턴
	if (ds_list.rowcount < 1) {
		gfn_showAlert("CW00010");
		return;
	}

	if ( !gfn_showConfirm("CI00010") ) {
		return;
	}

	var cnt = ds_list.GetRowCount();

	if (cnt > 0)
	{
		if ( length(ds_list.GetColumn(ds_list.row, "PJTID")) < 1 || length(ds_list.GetColumn(ds_list.row, "AUFNR")) < 1 )
		{
			gfn_showMsg("CW00017");
			return;
		}

	}

	tit_clearActionInfo();
	tit_addSearchActionInfo("wb04:WB0401001A_S7");

	tit_callService(
        ""
        , ""
        , ""
        , "ds_gubun5=ds_gubun5"
        , ""
        , ""
        , true
        , true
        , true);

	//ds_list.fireevent = false;


	//gfn_copyDs(ds_list, ds_list2, "CHECK=1");

	for (var i=0; i < ds_list.rowcount; i++)
	{
		if (ds_list.GetColumn(i,"CHECK") == "1")
		{
			for(var j=0; j < ds_gubun5.count;j++)
			{
				if ( ds_list.GetColumn(i,"PJTID") = ds_gubun5.GetColumn(j,"PJTID") && 
				     ds_list.GetColumn(i,"AUFNR") = ds_gubun5.GetColumn(j,"AUFNR"))
				{
					ds_list.SetColumn(i,"CHECK","1");

					break;		
				}
				else {
					ds_list.SetColumn(i,"CHECK","0");

				}

			}

			if (ds_list.GetColumn(i,"CHECK") = "0")
			 {
				 gfn_showAlert("CW00008", "프로젝트번호, 오더번호");
				 return;
			 }  

		}
	}


	//ds_list.fireevent = true;

	gfn_copyDs(ds_list, ds_list2, "CHECK=1");

	tit_clearActionInfo();
	tit_addSelectExActionInfo(
		"wb04:WB0401001A_S2"
	   ,"wb04:WB0401001A_I1"
	   ,"wb04:WB0401001A_U1");

	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2:A"
		, ""
		, ""
		, "fn_afterSave");


}
