function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	if ( strColumnID <> "CHECK"){
	//	obj.fireevent = false;

		var colid = strColumnID;
		cnt2 = nRow; 

	if ( Trim(obj.GetColumn(nRow,colid)) != Trim(ds_mm116.GetColumn(nRow,colid)))
	{	

		if ( colid == "PJTID" ){

			ds_cond2.SetColumn(0,"PJTID",varNewValue);
			ds_cond2.Setcolumn(0,"CNT",nRow);

			tit_clearActionInfo();
			tit_addSearchActionInfo("wb04:WB0401001A_S4");
			tit_addSearchActionInfo("wb04:WB0401001A_S6");

			tit_callService(
			""
			, ""
			, "ds_cond2=ds_cond2"
			, "ds_gubun2=ds_gubun2 ds_gubun4=ds_gubun4"
			, ""
			, "fn_refresh");

		} else if ( colid == "PT_NO" ){

			ds_cond3.SetColumn(0,"PT_NO",varNewValue);
			ds_cond3.SetColumn(0,"CNT",nRow);

			tit_clearActionInfo();
			tit_addSearchActionInfo("wb04:WB0401001A_S5");

			tit_callService(
			""
			, ""
			, "ds_cond3=ds_cond3"
			, "ds_gubun3=ds_gubun3"
			, ""
			, "fn_refresh2");

			}

		if ( ds_mm116.rowcount() > 0 ) {

			if ( nRow > ds_mm116.rowcount() - 1  ){

				obj.SetColumn(nRow,"CHECK","1");
				obj.SetColumn(nRow,"UDATE",G_CURR_DATE);
				obj.Setcolumn(nRow,"UNAME" ,G_USER_ID);
				obj.SetColumn(nRow,"PJTID" ,ds_list.GetColumn(0,"PJTID"));
				obj.SetColumn(nRow,"PJTXT",ds_list.GetColumn(0,"PJTXT"));
			} else {

				obj.Setcolumn(nRow,"CHECK","0");
				obj.SetColumn(nRow,"PJTID",ds_mm116.GetColumn(0,"PJTID"));
				obj.SetColumn(nRow,"PJTXT",ds_mm116.GetColumn(0,"PJTXT"));
			}
		} else {

			obj.SetColumn(nRow,"CHECK","1");
			obj.SetColumn(nRow,"UDATE",G_CURR_DATE);
			obj.SetColumn(nRow,"UNAME",G_USER_ID);

		}

		obj.SetColumn(nRow,"CHECK","1");
	 } else
		{
			obj.SetColumn(nRow,"PJTID",ds_list.GetColumn(0,"PJTID"));
			obj.SetColumn(nRow,"CHECK","0");
		}

		//obj.fireevent = true;		
	}

}

