function fn_OnLoadCompleted(obj) {
	gfn_initForm();

	tit_clearActionInfo();
	tit_addSearchActionInfo("wb05:WB0501002A_S2");      // 안전 권한

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_auth=ds_auth"  // ds_pronr=ds_pronr"
		, ""
		, "fn_afterInit"
		, ""
		, ""
		, true);

}


/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수
********************************************************************************/
function fn_afterInit() {

	// 안전 직원일 경우 -> 
	if(ds_auth.GetColumn(0, "CNT") > 0)
	{
		cal_chkdat.Enable = true;
	}
	else
	{
		cal_chkdat.Value = gfn_getCurrDate();
		cal_chkdat.Enable = false;
	}

	//  현황에서 호출된 경우
	if ( isExistVar("fa_posid", true) ) {
	   ed_posid.value = fa_posid;
	   cal_chkdat.value = fa_chkdat;

	   if(fa_zzgubun == 'L') // 제조일 경우 : WBS 비활성화 처리
	   {
			ed_posid.Enable = false;	// 프로젝트
			btn_popProj.Enable = false; // 프로젝트 돋보기 버튼
			ed_wk_desc.Enable = false;	// 작업내용
			ed_dept_n.Visible = false;
			cb_dept_n.Visible = true;
			ed_dpt_n.Visible = false;
			cb_dpt_n.Visible = true;
	   }
	   else
	   {
			ed_posid.Enable = true;	// 프로젝트
			btn_popProj.Enable = true; // 프로젝트 돋보기 버튼
			ed_wk_desc.Enable = true;	// 작업내용
			ed_dept_n.Visible = true;
			cb_dept_n.Visible = false;
			ed_dpt_n.Visible = true;
			cb_dpt_n.Visible = false;
	   }

       ds_cond.SetColumn(0,"ZZGUBUN", fa_zzgubun);
	   ds_cond.SetColumn(0, "CH_PER", fa_chper);
	   fn_query();
	}
}

