/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	if(gfn_isNull(ed_posid, true, Static0.Text)) return;
	if(gfn_isNull(cal_chkdat, true, Static2.Text)) return;
	if(gfn_isNull(ed_kind, true, Static29.Text)) return;

	if(Length(ed_posid.Text) < 9 && ed_kind.Value <> 'L') {
		gfn_showAlert("CW00001","호기번호 9자리 이상으로");
		return;
	}

	if(Length(trim(ed_per.Text)) == 0) {
		gfn_openMsgWin("점검자를 입력하세요.");
		ed_per.SetFocus();
		return;
	}

	switch(ed_kind.Value)
	{
		case "A" :
			ds_cond.SetColumn(0,"ZZGUBUN","A");
			break;
		case "B" :
			ds_cond.SetColumn(0,"ZZGUBUN","B");
			break;
		case "C" :
			ds_cond.SetColumn(0,"ZZGUBUN","C");
			break;
		case "D"  :
			ds_cond.SetColumn(0,"ZZGUBUN","D");
			break;
		case "E" :
			ds_cond.SetColumn(0,"ZZGUBUN","E");
			break;
		case "G" :	// 임원 - 설치
			ds_cond.SetColumn(0,"ZZGUBUN","G");
			break;
		case "H" :	// 임원 - 리모델링
			ds_cond.SetColumn(0,"ZZGUBUN","H");
			break;
		case "I" :	// 임원 - 서비스
			ds_cond.SetColumn(0,"ZZGUBUN","I");
			break;
		case "J" :	// 임원 - 제조
			ds_cond.SetColumn(0,"ZZGUBUN","J");
			break;
		case "K" :	// 임원 - 주차
			ds_cond.SetColumn(0,"ZZGUBUN","K");
			break;
		case "L" :	// 제조
			ds_cond.SetColumn(0,"ZZGUBUN","L");
			break;
	}

	ds_list.DeleteAll();

	tit_clearActionInfo();

	tit_addSearchActionInfo("wb05:WB0501001A_S1");
	tit_addSearchActionInfo("wb05:WB0501001A_S2");
	tit_addSearchActionInfo("wb05:WB0501001A_S3");
	tit_addSearchActionInfo("wb05:WB0501001A_S5");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_head=ds_head ds_list=ds_list ds_sum=ds_sum ds_fair=ds_fair"
        , ""
        , "fn_afterQuery"
        , "" //true
        , ""
        , true);
}

