/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {

	//Trace(ds_head.saveXML());
	if(ds_cond.GetUpdate() == true)
	{
		gfn_openMsgWin("조회 후 저장 가능합니다.");
		return;
	}

	if(ds_head.GetColumn(0,"BUMUN") == "") {

		gfn_showConfirm( "CW00001","사업부문");
		return;
	}
	if(ds_head.GetColumn(0,"WK_DESC") == "") {

		gfn_showConfirm( "CW00001","작업내용");
		return;
	}


	if(ed_kind.Value <> 'L')	// 제조일 경우에는 미체크 
	{
		if(ds_head.GetColumn(0,"WK_PER") == "") {
			gfn_showConfirm( "CW00001","작업자");
			return;
		}
		if(ds_head.GetColumn(0,"CH_NO") == "") {
			gfn_showConfirm( "CW00001","작업자수");
			return;
		}
	}

	if(ds_head.GetColumn(0,"EVAL_GRADE") < 0 ) {
		gfn_showConfirm( "CW00008", "총점수");
		return;
	}

	if(ed_kind.Value == "A"){
		if (ds_list.GetColumn(0,"ZZGUBUN") != "A"){
		    gfn_showConfirm( "CW00001","작업유형과 체크리스트");
		    return;
		}
	}
	if(ed_kind.Value == "B"){
		if (ds_list.GetColumn(0,"ZZGUBUN") != "B"){
			gfn_showConfirm( "CW00001","작업유형과 체크리스트");
			return;
		}
	}
	if(ed_kind.Value == "C"){
		if (ds_list.GetColumn(0,"ZZGUBUN") != "C"){
			gfn_showConfirm( "CW00001","작업유형과 체크리스트");
			return;
		}
	}
	if(ed_kind.Value == "D"){
		if (ds_list.GetColumn(0,"ZZGUBUN") != "D"){
			gfn_showConfirm( "CW00001","작업유형과 체크리스트");
			return;
		}
	}
	if(ed_kind.Value == "E"){
		if (ds_list.GetColumn(0,"ZZGUBUN") != "E"){
			gfn_showConfirm( "CW00001","작업유형과 체크리스트");
			return;
		}
	}
	if(ed_kind.Value == "G"){
		if (ds_list.GetColumn(0,"ZZGUBUN") != "G"){
			gfn_showConfirm( "CW00001","작업유형과 체크리스트");
			return;
		}
	}


	var im_No_cnt = 0;

	for(var i=0; i<ds_list.rowcount; i++) {
		if(ds_list.GetColumn(i,"ZQRES") == "2" && length(ds_list.GetColumn(i,"ZQDESC")) < 2) {
			gfn_showConfirm( "CW00001","부적합내용");
			return;
		}

//		if(ds_list.GetColumn(i,"ZQRES") == "2" && ds_list.GetColumn(i,"FILE") == '사진등록') {
//			gfn_showConfirm('결과를 NO로 한 경우 사진을 등록해야 합니다.');
//			return;
//		}

		if(ds_list.GetColumn(i, "ZFLAG") == "Y" && ds_list.GetColumn(i, "ZQNO") == true) {
			im_No_cnt++;
		}
	}

	ds_head.SetColumn(0, "EVAL_IMNO", im_No_cnt);
	ds_head.SetColumn(0, "CH_PER", trim(ed_per.Text));


	if(ck_input.Value == "1")
	{
		ds_head.SetColumn(0,"DIRECT_INPUT", "1"); // 체크해서 직접 입력
	}	
	else
	{
		ds_head.SetColumn(0,"DIRECT_INPUT", "");
	}	

	 alert(ds_head.GetColumn(0,"DIRECT_INPUT"));
	trace(ds_head.SaveXML());


	switch(ed_kind.Value)
	{
		case "A" :
			ds_head.SetColumn(0,"WK_KIND","EL/ES");
			break;
		case "B" :
			ds_head.SetColumn(0,"WK_KIND","주차");
			break;
		case "C" :
			ds_head.SetColumn(0,"WK_KIND","물류");
			break;
		case "D"  :
			ds_head.SetColumn(0,"WK_KIND","PSD");
			break;
		case "E" :
			ds_head.SetColumn(0,"WK_KIND","서비스");
			break;
		case "G" :
			ds_head.SetColumn(0,"WK_KIND","임원-설치");
			break;
		case "H" :
			ds_head.SetColumn(0,"WK_KIND","임원-리모델링");
			break;
		case "I" :
			ds_head.SetColumn(0,"WK_KIND","임원-서비스");
			break;
		case "J" :
			ds_head.SetColumn(0,"WK_KIND","임원-제조");
			break;
		case "K" :
			ds_head.SetColumn(0,"WK_KIND","임원-주차");
			break;
	    case "L" :
			ds_head.SetColumn(0,"WK_KIND","제조");
			break;
	}
	//해당데이타를 저장하시겠습니까?

	if(!gfn_showConfirm( "CI00014", "해당 데이터" )) return;

	tit_clearActionInfo();
	tit_addMultiActionInfo("wb05:WB0501001A_I1");
	tit_addMultiActionInfo("wb05:WB0501001A_I2");
	tit_callService(
			""
			, ""
			, "ds_head=ds_head ds_list=ds_list ds_cond=ds_cond"
			, ""
			, ""
			, "fn_afterSave"
			, ""
			, ""
			, true);

}

