	gfn_saveToExcel(grd_list, true);
}

/********************************************************************************
* 기      능   : 취소 35
********************************************************************************/
function fn_cancel() {
	ds_head.Reset();
	ds_list.Reset();

 	// init data set
	ds_cond.SetColumn(0, "REP", "NH");
	ds_head.SetColumn(0, "BJD", gfn_getCurrDate());
	ds_head.SetColumn(0, "HDM", gfn_getCurrDate());
	ds_head.SetColumn(0, "SNM", G_USER_NAME);

	gfn_initDs(ds_list, true);

	Radio1.Index = -1;
	Radio2.Index = -1;
}

/********************************************************************************
* 기      능   : print 40
********************************************************************************/
function fn_print() {
	ds_head2.ClearData();
	ds_list2.ClearData();
	ds_sum2.ClearData();

	ds_head2.AddRow();
	ds_head2.SetColumn(0, "BUMUN", ds_head.GetColumn(0, "BUMUN"));
	ds_head2.SetColumn(0, "CH_PER", ds_head.GetColumn(0, "CH_PER"));
	ds_head2.SetColumn(0, "CH_MANAGER", ds_head.GetColumn(0, "CH_MANAGER"));
	ds_head2.SetColumn(0, "CHKDAT", ds_head.GetColumn(0, "CHKDAT"));
	ds_head2.SetColumn(0, "DEPT_N", ds_head.GetColumn(0, "DEPT_N"));
	ds_head2.SetColumn(0, "DPT_N", ds_head.GetColumn(0, "DPT_N"));
	ds_head2.SetColumn(0, "LIFNR_N", ds_head.GetColumn(0, "LIFNR_N"));
	ds_head2.SetColumn(0, "PM_N", ds_head.GetColumn(0, "PM_N"));
	ds_head2.SetColumn(0, "POSID", ds_head.GetColumn(0, "POSID"));
	ds_head2.SetColumn(0, "WK_DESC", ds_head.GetColumn(0, "WK_DESC"));
	ds_head2.SetColumn(0, "WK_KIND", ds_head.GetColumn(0, "WK_KIND"));
	ds_head2.SetColumn(0, "WK_PER", ds_head.GetColumn(0, "WK_PER"));
	ds_head2.SetColumn(0, "ZSITE_NM", ds_head.GetColumn(0, "ZSITE_NM"));
	ds_head2.SetColumn(0, "ED_MA1", ds_head.GetColumn(0, "ED_MA1"));
	ds_head2.SetColumn(0, "ED_MA2", ds_head.GetColumn(0, "ED_MA2"));
	ds_head2.SetColumn(0, "ED_MA3", ds_head.GetColumn(0, "ED_MA3"));
	ds_head2.SetColumn(0, "ED_WK1", ds_head.GetColumn(0, "ED_WK1"));
	ds_head2.SetColumn(0, "ED_WK2", ds_head.GetColumn(0, "ED_WK2"));
	ds_head2.SetColumn(0, "ED_WK3", ds_head.GetColumn(0, "ED_WK3"));
	ds_head2.SetColumn(0, "ACT_WARN_CD", ds_head.GetColumn(0, "ACT_WARN_CD"));
	ds_head2.SetColumn(0, "ACT_WARN_DESC", ds_head.GetColumn(0, "ACT_WARN_DESC"));
	ds_head2.SetColumn(0, "ACT_STOP_CD", ds_head.GetColumn(0, "ACT_STOP_CD"));
	ds_head2.SetColumn(0, "ACT_STOP_DESC", ds_head.GetColumn(0, "ACT_STOP_DESC"));
	ds_head2.SetColumn(0, "BIGO", ds_head.GetColumn(0, "BIGO"));
	ds_head2.SetColumn(0, "EVAL_GRADE", ds_head.GetColumn(0, "EVAL_GRADE"));

	var t_rpt = "";
	ds_list2.AddRow();

	for(var i=0; i<ds_list.rowcount; i++) {
		if(fa_zzgubun == "A") { // EL/ES
			t_rpt = "WB05::WB0501002B.xml";

			if(i ==  0) { ds_list2.SetColumn(0, "A01", ds_list.GetColumn(i, "ZQRES")); ds_list2.SetColumn(0, "A01_DESC", ds_list.GetColumn(i, "ZQDESC")); }
			if(i ==  1) { ds_list2.SetColumn(0, "A02", ds_list.GetColumn(i, "ZQRES")); ds_list2.SetColumn(0, "A02_DESC", ds_list.GetColumn(i, "ZQDESC")); }
			if(i ==  2) { ds_list2.SetColumn(0, "A03", ds_list.GetColumn(i, "ZQRES")); ds_list2.SetColumn(0, "A03_DESC", ds_list.GetColumn(i, "ZQDESC")); }
			if(i ==  3) { ds_list2.SetColumn(0, "A04", ds_list.GetColumn(i, "ZQRES")); ds_list2.SetColumn(0, "A04_DESC", ds_list.GetColumn(i, "ZQDESC")); }
			if(i ==  4) { ds_list2.SetColumn(0, "A05", ds_list.GetColumn(i, "ZQRES")); ds_list2.SetColumn(0, "A05_DESC", ds_list.GetColumn(i, "ZQDESC")); }
			if(i ==  5) { ds_list2.SetColumn(0, "A06", ds_list.GetColumn(i, "ZQRES")); ds_list2.SetColumn(0, "A06_DESC", ds_list.GetColumn(i, "ZQDESC")); }
			if(i ==  6) { ds_list2.SetColumn(0, "A07", ds_list.GetColumn(i, "ZQRES")); ds_list2.SetColumn(0, "A07_DESC", ds_list.GetColumn(i, "ZQDESC")); }
