		if(i ==  3) { ds_sum2.SetColumn(0, "OK4", ds_sum.GetColumn(i, "OK")); ds_sum2.SetColumn(0, "NO4", ds_sum.GetColumn(i, "NO")); ds_sum2.SetColumn(0, "NA4", ds_sum.GetColumn(i, "NA")); }
		if(i ==  4) { ds_sum2.SetColumn(0, "OK5", ds_sum.GetColumn(i, "OK")); ds_sum2.SetColumn(0, "NO5", ds_sum.GetColumn(i, "NO")); ds_sum2.SetColumn(0, "NA5", ds_sum.GetColumn(i, "NA")); }
		if(i ==  5) { ds_sum2.SetColumn(0, "OK6", ds_sum.GetColumn(i, "OK")); ds_sum2.SetColumn(0, "NO6", ds_sum.GetColumn(i, "NO")); ds_sum2.SetColumn(0, "NA6", ds_sum.GetColumn(i, "NA")); }
		if(i ==  6) { ds_sum2.SetColumn(0, "OK7", ds_sum.GetColumn(i, "OK")); ds_sum2.SetColumn(0, "NO7", ds_sum.GetColumn(i, "NO")); ds_sum2.SetColumn(0, "NA7", ds_sum.GetColumn(i, "NA")); }
		if(i ==  7) { ds_sum2.SetColumn(0, "OK8", ds_sum.GetColumn(i, "OK")); ds_sum2.SetColumn(0, "NO8", ds_sum.GetColumn(i, "NO")); ds_sum2.SetColumn(0, "NA8", ds_sum.GetColumn(i, "NA")); }
		if(i ==  8) { ds_sum2.SetColumn(0, "OK9", ds_sum.GetColumn(i, "OK")); ds_sum2.SetColumn(0, "NO9", ds_sum.GetColumn(i, "NO")); ds_sum2.SetColumn(0, "NA9", ds_sum.GetColumn(i, "NA")); }
	}

	Dialog(t_rpt,"", -1, -1, "Resize=true");
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 메시지 처리
	//gfn_showSearchMsg(ed_zsite_nm);

	// if( ds_head.rowcount <= 0 )
	// {
		// gfn_initDs(ds_head, true);
		// ufn_getWbsInfo();
	// }
	var date_flag = false;


	if(gfn_getBeforeDate(gfn_getCurrDate(), 364) <= ds_cond.GetColumn(0,"CHKDAT")) { // 364일 이내 신규입력 및 수정가능 체크(2018-12-14 김지환 sw요청으로 인해 7일에서 364일로 변경)
		date_flag = true;
	} else {
		date_flag = false;
	}


	if(ds_head.rowcount > 0) { //수정
		if(date_flag = true) {
			ufn_setControl(true);
		} else {
			ufn_setControl(false);
		}
	} else { //신규
		if(date_flag = true) {
			ufn_setControl(true);
		} else {
			ufn_setControl(false);
		}
	}

	if(g_user_id == "2011662" || g_user_id == "2025558" || g_user_id == "2023709") {
		ufn_setControl(true);
	}

	if( ds_head.rowcount <= 0)
	{
		gfn_initDs(ds_head, true);
		ufn_getWbsInfo();
	}

	if (ds_fair.GetColumn(0, 'FAIR') == 'N') {
		ed_wk_desc.Enable = true;
		ed_wk_desc.Right = 1060;
		btnGoFair.Visible = false;
	} else {
		ed_wk_desc.Enable = true;
		ed_wk_desc.Right = 998;
		btnGoFair.Visible = true;
	}

	// 제조부문 추가

	if(ed_kind.Value == 'L')
	{
		ed_pm_n.Enable = false;		// pm명
		ed_wk_per.Enable = false;	// 작업자
		ed_ch_no.Enable = false;	// 작업자수
		ed_zsite_nm.Enable = false;

		if(ds_head.GetColumn(0,"DIRECT_INPUT")	== "1")
		{
			ed_dept_n.Visible = true;
			cb_dept_n.Visible = false;
			ed_dpt_n.Visible = true;
			cb_dpt_n.Visible = false;

		}
		else
		{

			ed_dept_n.Visible = false;
			cb_dept_n.Visible = true;
			ed_dpt_n.Visible = false;
			cb_dpt_n.Visible = true;
		}
	}
	else
	{
		ed_pm_n.Enable = true;		// pm명
		ed_wk_per.Enable = true;	// 작업자
		ed_ch_no.Enable = true;	// 작업자수
		ed_zsite_nm.Enable = false;
	}




}

/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg)
{
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
	}
}

function fn_afterWbsQuery(errCode, errMsg) {
	if( ds_head.rowcount <= 0 && ed_kind.Value <> 'L')
	{
		gfn_showMsg("CW00010");
		gfn_initDs(ds_head, true);
		gfn_initDs(ds_list, true);
		gfn_initDs(ds_sum, true);
	}


	if(ed_kind.Value == 'L')
	{
		ds_head.AddRow();
		ds_head.SetColumn(0, "MANDT", '183');
		ds_head.SetColumn(0, "POSID", ed_posid.Text);
		ds_head.SetColumn(0, "CHKDAT", cal_chkdat.Value);
		ds_head.SetColumn(0, "ZZGUBUN", ed_kind.Value);
		ds_head.SetColumn(0, "CH_PER", ed_per.Text);

	}


}

/********************************************************************************
* 기      능   : 삭제 Callback
********************************************************************************/
function fn_afterDelete(errCode, errMsg)
{

}

