function ufn_setControl(flg) {
	if(flg = true) {
		grd_list1.Editable = true;
		cb_ed_ma1.enable = true;
		cb_ed_ma2.enable = true;
		cb_ed_ma3.enable = true;
		cb_ed_wk1.enable = true;
		cb_ed_wk2.enable = true;
		cb_ed_wk3.enable = true;
		chk_act_warn_cd.enable = true;
		chk_act_stop_cd.enable = true;
		ed_act_warn_desc.enable = true;
		ed_act_stop_desc.enable = true;
		cb_bumun.enable = true;
		ed_dpt_n.enable = true;
		ed_dept_n.enable = true;
		ed_pm_n.enable = true;
		ed_lifnr_n.enable = true;
		//ed_wk_desc.enable = true;
		//ed_ch_per.enable = true;
		ed_wk_per.enable = true;
		ed_chmanager.Enable = true;
		ed_ch_no.Enable = true;
		ck_remodel.Enable = true;
		ed_bigo.Enable = true;
	} else {
		grd_list1.Editable = false;
		cb_ed_ma1.enable = false;
		cb_ed_ma2.enable = false;
		cb_ed_ma3.enable = false;
		cb_ed_wk1.enable = false;
		cb_ed_wk2.enable = false;
		cb_ed_wk3.enable = false;
		chk_act_warn_cd.enable = false;
		chk_act_stop_cd.enable = false;
		ed_act_warn_desc.enable = false;
		ed_act_stop_desc.enable = false;
		cb_bumun.enable = false;
		ed_dpt_n.enable = false;
		ed_dept_n.enable = false;
		ed_pm_n.enable = false;
		ed_lifnr_n.enable = false;
		//ed_wk_desc.enable = false;
		//ed_ch_per.enable = false;
		ed_wk_per.enable = false;
		ed_chmanager.Enable = false;
		ed_ch_no.Enable = false;
		ck_remodel.Enable = false;
		ed_bigo.Enable = false;
	}
}

