    }
}

function ed_hno_OnKillFocus(obj)
{
	if(length(ed_hno.Text)>8) fn_query();
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var ok = 0;
    var no = 0;
    var na = 0;

	var im_Ncnt = 0;
	var okcnt = 0;
	var nocnt = 0;
	var score = 0;

	switch(strColumnID)
	{
		case "ZQOK" :
			if(varNewValue == "1") {
				ds_list.SetColumn(nRow,"ZQRES","1");
				ds_list.SetColumn(nRow,"ZQNO","");
			}
			else if (ds_list.GetColumn(nRow,"ZQOK") == "0"){
				ds_list.SetColumn(nRow,"ZQRES","");
			}
			break;
		case "ZQNO" :
			if(varNewValue == "1") {
				ds_list.SetColumn(nRow,"ZQRES","2");
				ds_list.SetColumn(nRow,"ZQOK","");
			}
			else if (ds_list.GetColumn(nRow,"ZQNO") == "0") {
				ds_list.SetColumn(nRow,"ZQRES","");
			}
			break;
	}

	for(i = 0; i < ds_list.rowcount(); i++)
	{
		if (ds_list.GetColumn(i,"ZQRES") == "1") {
			okcnt++;
			if(ds_list.GetColumn(i,"ZQGUBN") == ds_list.GetColumn(nRow,"ZQGUBN")) ok++;
		}
		else if (ds_list.GetColumn(i,"ZQRES") == "2") {
			nocnt++;
			if(ds_list.GetColumn(i,"ZQGUBN") == ds_list.GetColumn(nRow,"ZQGUBN")) no++;
			if(ds_list.GetColumn(i,"ZFLAG") == "Y") im_Ncnt++;
		}
		else {
			if(ds_list.GetColumn(i,"ZQGUBN") == ds_list.GetColumn(nRow,"ZQGUBN")) na++;
		}
	}
	ds_sum.SetColumn(ds_sum.FindRow("ZQGUBN",ds_list.GetColumn(nRow,"ZQGUBN")), "OK", ok);
	ds_sum.SetColumn(ds_sum.FindRow("ZQGUBN",ds_list.GetColumn(nRow,"ZQGUBN")), "NO", no);
	ds_sum.SetColumn(ds_sum.FindRow("ZQGUBN",ds_list.GetColumn(nRow,"ZQGUBN")), "NA", na);

	score = Round((okcnt*100)/(okcnt+nocnt))-(im_Ncnt*5);

	// 최저 점수 마이너스 값이 아닌 0 으로 수정 2017.01.09 yw
	if(score < 0)
	{
		score = 0;
	}

	ds_head.SetColumn(0,"EVAL_GRADE", score);
	ds_head.SetColumn(0,"EVAL_OK", grd_sum.GetCellText("summary", 0,1));
	ds_head.SetColumn(0,"EVAL_NO", grd_sum.GetCellText("summary", 0,2));
	ds_head.SetColumn(0,"EVAL_IMNO", im_Ncnt);

	// if(strColumnID == "ZQRES"){
		// ed_focus.SetFocus();
	// }
}

/********************************************************************************
* 기      능   :
********************************************************************************/

function ds_head_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "ED_MA1" || strColumnID == "ED_MA2" || strColumnID == "ED_MA3" ||
	   strColumnID == "ED_WK1" || strColumnID == "ED_WK2" || strColumnID == "ED_WK3" )
    {
		ed_focus.SetFocus();
	}
}

function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (nCell == 5) {
		var arg = "POSID="  + quote(ds_cond.GetColumn(0,"POSID"))
				+ " CHKDAT=" + quote(ds_cond.GetColumn(0,"CHKDAT"))
				+ " ZZGUBUN=" + quote(ds_cond.GetColumn(0,"ZZGUBUN"))
				+ " CH_PER=" + quote(ds_cond.GetColumn(0,"CH_PER"))
				+ " ZQNUM=" + quote(ds_list.GetColumn(nRow,"ZQNUM"));

		gfn_openPopup("PS0201001J", true, arg);
	}
}

function btn_go_fair_OnClick(obj)
{
	if (ds_head.RowCount() == 0) {
		gfn_showConfirm('먼저 조회해 주십시오.');
		return;
	}

	ds_cond.SetColumn(0, 'INP_DT', ds_cond.GetColumn(0, 'CHKDAT'));
	ds_cond.SetColumn(0, 'ZZLIFNR', ds_head.GetColumn(0, 'ZZLIFNR'));
	ds_cond.SetColumn(0, 'TMCD', ds_head.GetColumn(0, 'TMCD'));

    tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0208001A_S3"); //PS0207001A_S3 기존파일
	tit_addSearchActionInfo("ps02:PS0207001A_S10");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list1=ds_list1 ds_list3=ds_list3"
		, ""
		, "fn_after_btn_go_fair_OnClick"
		, ""  //true
		, ""
		, true);
}

function fn_after_btn_go_fair_OnClick() {
    var chk = 'Y';
    var inwon  = 'N';
    var active = 'N'; // 투입에 따른 공정표 활성화 플레그 (신규 공정표 화면(PS0208001B)의 확인버튼 보이게/안보게 처리)
    var percnt = 0;
    var pro = 0;

	var nChkInwon = 0;
	var nChkTime  = 0;

	var args = ' F_POSID='+quote(ds_list1.getColumn(0, "POSID"));    // 호기번호
	args += ' F_GUBUN='+quote(ds_list1.getColumn(0, "ZZGUBUN"));
	args += ' F_TYPE='+quote(ds_list1.getColumn(0, "TYPE"));
	args += ' F_VER='+quote(ds_list1.getColumn(0, "VER"));	         //버전
	args += ' F_INP='+quote(ds_cond.getColumn(0, "INP_DT"));
	args += ' F_CHK='+quote(chk);										//미입력분, 입력분 구분
	args += ' F_PRO='+quote(ds_list1.getColumn(0, "DAY_R"));
	args += ' F_SAFE='+quote(ds_list1.getColumn(0, "SAFE"));
	args += ' F_PSUM='+quote(pro);
	args += ' F_PCNT='+quote(percnt);
	args += ' F_INWON="0"';
	args += ' F_INWON_CHK='+quote(inwon);
	args += ' F_ACTIVE='+quote(active);
	args += ' F_CHKTIME='+quote(nChkTime);
	args += ' F_CHKINWON='+quote(nChkInwon);
	args += ' F_PSPID='+quote(Substr(ds_list1.getColumn(0, "POSID"), 0, 6));
	args += ' F_SELECTABLE=Y';
	args += ' F_TITLE=' + quote('공정선택 (아래 항목에서 더블클릭하여 선택하세요)');

	//공정표 팝업의 확인버튼 보여 줄 경우 (N: 보여줌)

	trace("args : " + args);

	var pType = ds_list1.getColumn(0, "TYPE");
	var pVer = ds_list1.getColumn(0, "VER");

	if(pVer == "000")
	{ // 기존타입 ( 일정기간 동안 신규타입 기존 타입을 같이사용)

		if(ds_list1.getColumn(0, "TYPE") <> '')
		{
			if(ds_list1.getColumn(0, "ZZGUBUN") == "10")
			{
				gfn_openPopup("PS0207001B", true, args);
			}
			else if(ds_list1.getColumn(0, "ZZGUBUN") == "11")
			{
				gfn_openPopup("PS0207001C", true, args);
			}
			else if(ds_list1.getColumn(0, "ZZGUBUN") == "12")
			{
				gfn_openPopup("PS0207001D", true, args);
			}
		}
	} else {
		if(ds_list1.getColumn(0, "TYPE") <> '')
		{
			gfn_openPopUpUrlHdel("PS02::PS0208001B.xml", true, args);
		}
	}
}

function fn_set_ed_wk_desc(val) {
	ed_wk_desc.text = val;
}

function onResume(_ZQNUM, isFileUploaded) {
	trace('_ZQNUM : ' + _ZQNUM + ', isFileUploaded : ' + isFileUploaded);
	var rownum = -1;
	if (isFileUploaded == '1') {
		for (var i = 0; i < ds_list.RowCount(); i++) {
			if (ds_list.GetColumn(i, 'ZQNUM') == _ZQNUM) {
				ds_list.SetColumn(i, 'FILE', '사진보기');
			}
		}
	}
}


function ed_kind_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strCode == 'L')	//제조로 변경 시
	{
		// WBS Clear
		ed_posid.Enable = false;	// 프로젝트
		btn_popProj.Enable = false; // 프로젝트 돋보기 버튼
		ed_wk_desc.Enable = false;	// 작업내용
		ed_posid.Text = "PLANT";
		ed_dept_n.Visible = false;
		cb_dept_n.Visible = true;
		ed_dpt_n.Visible = false;
		cb_dpt_n.Visible = true;
		ck_input.Visible = false;
		ck_input.Value = 0;
	}
	else
	{
		ed_posid.Enable = true;
		btn_popProj.Enable = true;
		ed_wk_desc.Enable = true;	// 작업내용
		ed_posid.Text = "";
		ed_dept_n.Visible = true;
		cb_dept_n.Visible = false;
		ed_dpt_n.Visible = true;
		cb_dpt_n.Visible = false;
		ck_input.Visible = false;
		ck_input.Value = 0;
	}
}

function cb_bumun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(cb_bumun.Value == "제조")
	{
		ed_dept_n.Visible = false;
		cb_dept_n.Visible = true;
		ed_dpt_n.Visible = false;
		cb_dpt_n.Visible = true;

		ck_input.Visible = true;
		ck_input.Value = 0;
	}
	else
	{
		ed_dept_n.Visible = true;
		cb_dept_n.Visible = false;
		ed_dpt_n.Visible = true;
		cb_dpt_n.Visible = false;

		ck_input.Visible = false;
		ck_input.Value = 0;
	}
}

function ck_input_OnClick(obj,strValue)
{
	if(ck_input.Value == "0")
	{
		ed_dept_n.Visible = false;
		cb_dept_n.Visible = true;
		ed_dpt_n.Visible = false;
		cb_dpt_n.Visible = true;
	}
	else
	{
		ed_dept_n.Visible = true;
		cb_dept_n.Visible = false;
		ed_dpt_n.Visible = true;
		cb_dpt_n.Visible = false;
	}
}
