/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {

	var nRow = ds_list.currow;

	if ( nRow > -1 ) {	
		var sRowType = ds_list.GetRowType(nRow);
		if ( sRowType == "insert" ) {

			var sFileName = ds_list.GetColumn(nRow, "FILENM");

			if(length(sFileName) != 0) {
				var arr = tit_fileDelete("PS",ds_list.getColumn(nRow, "FILENM_APP"), false);
			}

			ds_list.DeleteRow(nRow);
		} else {
			ds_list.SetColumn(nRow, "FLAG", "D");		
		}			
	}		


}

