********************************************************************************/
function fn_afterQuery(errCode, errMsg) {

	if ( ds_list.rowCount() > 0 ) {
		gfn_showMsg("CI00002", ds_list.rowCount() + "");

		ds_list.AddColumn("SEQ", "INT","20");

		for(var i=0 ; i < ds_list.rowCount() ; i++ ) {
			// SetColumn(nRow,strColID,varVal);

			ds_list.SetColumn(i,"SEQ", toInteger(ds_list.GetColumn(i,"EVTSEQ")));
		}



	} else {
		gfn_showMsg("CW00010");
	}

}

/********************************************************************************
* 기      능   : 프로젝트 기본 조회 Callback  
********************************************************************************/
function fn_afterQueryPrj(errCode, errMsg) {

	if (  ds_head.rowCount() > 0 ) {

		// 그리드에 row 추가 
		var cnt = ds_list.GetRowCount();

		var row = ds_list.AddRow();
		//ds_list.SetColumn(ds_list.row, "FLAG", "I");
		//ds_list.RowPos = cnt;

		//ds_list.CopyRow(row, ds_list2, ds_list2.currow);				
		ds_list.CopyRow(row, ds_head, ds_head.currow);
		ds_list.SetColumn(row, "FLAG", "I");
		ds_list.SetColumn(row, "CHECK", "1");

		ds_list.SetColumn(row,"SEQ", toInteger(ds_list.GetColumn(row,"EVTSEQ")));

		ds_list.RowPos = row;

		//grd_list.SetCellProp("Body",0,"Edit","none");
		//grd_list.Redraw("Body", row);



	} else {
		gfn_showMsg("CW00010");
	}

}
