/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else if ( nCell == 2 ) {
		// No. 숫자

		var nheadText,sflag;
		//  해당하는 셀의 타이틀의 소트마크를 추가 또는 변경한다.
		if(right(obj.GetCellProp("head",nCell,"text"),1) == CONST_ASC_MARK) 
		{
			sflag = CONST_DESC_MARK;
		} 
		else 
		{
			sflag = CONST_ASC_MARK;
		}

		var dsObj = object(obj.bindDataset);
		// 데이터셋의 rowcount가 1보다 작을 경우
		if ( dsObj.rowCount() < 2 ) 
		{
			return;
		}
		var HeadCol = obj.GetCellProp("Head",nCell,"col");
		//  해당하는 셀의 타이틀의 소트마크를 추가 또는 변경한다.
		if(sflag== CONST_DESC_MARK) 
		{

			trace( "HeadCol =>" +HeadCol + " , type = > " + dsObj.GetColType("EVTSEQ") );
			dsObj.sort(obj.GetCellProp("Body",HeadCol,"colid"),false);
			nheadText = obj.GetCellProp("head",nCell,"text");
			nheadText = replace(nheadText,CONST_ASC_MARK,"");
			nheadText = replace(nheadText,CONST_DESC_MARK,"");
			nheadText = nheadText + CONST_DESC_MARK;
		}
		else 
		{
			trace( "HeadCol =>" +HeadCol + " , type = > " + dsObj.GetColType("EVTSEQ") );
			dsObj.sort(obj.GetCellProp("Body",HeadCol,"colid"),true);
			nheadText = obj.GetCellProp("head",nCell,"text");
			nheadText = replace(nheadText,CONST_ASC_MARK,"");
			nheadText = replace(nheadText,CONST_DESC_MARK,"");
			nheadText = nheadText + CONST_ASC_MARK;
		}

		obj.SetCellProp("head",nCell,"text",nheadText);
		gfn_clearGridSortMark( obj, nCell );		

	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/

function ds_list_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	if ( strColumnID == "CHECK"){

		var flag = ds_list.GetColumn(nRow,"FLAG");

		if( flag == 'I' ) {		
			trace("ds_list_CanColumnChange flag I");
			return;
		} 

	}

}

