function btn_upload_OnClick(obj)
{
	grd_list.Format = "Upload";

	ds_excel.ClearData();
	ds_mm116.ClearData();

	Create("FileDialog", "_fileDialog_", "");
	var dialogObj = object("_fileDialog_");

	dialogObj.Type = "Open";
	dialogObj.FileName = "";
	dialogObj.Filter = "Microsoft Excel File(*.xls)|*.xls|";
	dialogObj.open();

	if (length(dialogObj.FileName) == 0) {
		Destroy( "_fileDialog_" );
		return;
	}

	var sFileName = dialogObj.FilePath + "\\" + dialogObj.FileName;

	ext_ExcelImportByIndex(sFileName,null,"ds_excel",0,1);

	grd_list.BindDataset = "";

	ufn_ExcelToList();

	grd_list.BindDataset = "ds_list";

	Destroy( "_fileDialog_" );  // 불러오기후 dialog 제거

}
/************************************************************************************/
// Excel data grid에 복사 작업
/************************************************************************************/
function ufn_ExcelToList(){

	ds_list.ClearData();

	grd_list.SetCellProp("head", 0, "Text", "1");

	for(i=1; i < ds_excel.rowcount ; i++) {
		ds_list.AddRow();
		ds_list.SetColumn(i-1,"PJTID", ds_excel.GetColumn(i,0));
	//	ds_list.SetColumn(i-1,"PJTXT", ds_excel.GetColumn(i,1));
		ds_list.SetColumn(i-1,"AUFNR", ds_excel.GetColumn(i,1));
		ds_list.SetColumn(i-1,"PT_GBN", ds_excel.GetColumn(i,2));
		ds_list.SetColumn(i-1,"PT_NO", ds_excel.GetColumn(i,3));
	//	ds_list.SetColumn(i-1,"PT_NM", ds_excel.GetColumn(i,4));
		ds_list.SetColumn(i-1,"MENGE", ds_excel.GetColumn(i,4));
		ds_list.SetColumn(i-1,"NETPR", ds_excel.GetColumn(i,5));
		ds_list.SetColumn(i-1,"BIGO", ds_excel.GetColumn(i,6));
	}

}


