/********************************************************************************
* 기      능   : 그리드 셀 위치 변경 
********************************************************************************/
function grd_list_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{

	// 수정모드 확인 	
	if( obj.GetCellValue(nRow,0) != '1') {
		return;
	}

	trace("CellPosChanged nRow => " + nRow + " , nCell => " + nCell + ", nOldRow => " + nOldRow + " , nOldCell => " + nOldCell + " , value => " + obj.GetCellValue(nRow,nCell) + ", chk => " + obj.GetCellValue(nRow,0) );

	// 사고 구분2
	if( nCell == 26 ) {

		var gbn1 =  ds_list.GetColumn(nRow, "EVT_GBN1");

		if( gbn1 == "E/L" || gbn1 == "E/S" || gbn1 == "주차"  ){
			grd_list.SetCellProp('Body', '26' , 'ComboDataset', 'ds_evt_gbn21');		
		} else if( gbn1 == "제조"){
			grd_list.SetCellProp('Body', '26' , 'ComboDataset', 'ds_evt_gbn22');
		} else if( gbn1 == "도급"){
			grd_list.SetCellProp('Body', '26' , 'ComboDataset', 'ds_evt_gbn23');
		} else if( gbn1 == "기타"){
			grd_list.SetCellProp('Body', '26' , 'ComboDataset', 'ds_evt_gbn24');
		} else {
			grd_list.SetCellProp('Body', '26' , 'ComboDataset', '');
		}

	}
	// 타입 
	if( nCell == 27 ) {

		var gbn1 =  ds_list.GetColumn(nRow, "EVT_GBN1");
		var gbn2 =  ds_list.GetColumn(nRow, "EVT_GBN2");


		if( gbn2 == "신규" || gbn2 == "MOD" || gbn2 == "유지보수(서비스)" || gbn2 == "부품수리" ){
			grd_list.SetCellProp('Body', '27' , 'ComboDataset', 'ds_evt_gbn31');
		} else if( gbn2 == "리모델링"){
			grd_list.SetCellProp('Body', '27' , 'ComboDataset', 'ds_evt_gbn32');
		} else {
			grd_list.SetCellProp('Body', '27' , 'ComboDataset', '');
		}	

		if( gbn1 != 'E/L') {
			grd_list.SetCellProp('Body', '27' , 'ComboDataset', 'ds_evt_nl');
		}


	}
	// 기종 
	if( nCell == 28 ) {	

		var gbn3 =  ds_list.GetColumn(nRow, "EVT_GBN3");
		var gbn1 =  ds_list.GetColumn(nRow, "EVT_GBN1");

		if( gbn3 == "MR"){
			grd_list.SetCellProp('Body', '28' , 'ComboDataset', 'ds_evt_gbn41');
		} else if( gbn3 == "MRL"){
			grd_list.SetCellProp('Body', '28' , 'ComboDataset', 'ds_evt_gbn42');
		} else if( gbn3 == "레일 재사용" || gbn3 == "레일철거 및 교체"){
			grd_list.SetCellProp('Body', '28' , 'ComboDataset', 'ds_evt_gbn43');
		} else {
			grd_list.SetCellProp('Body', '28' , 'ComboDataset', '');
		}

		if( gbn1 == "주차"){
			grd_list.SetCellProp('Body', '28' , 'ComboDataset', 'ds_evt_gbn44');
		}

		if( gbn1 != 'E/L' &&  gbn1 != '주차') {
			grd_list.SetCellProp('Body', '28' , 'ComboDataset', 'ds_evt_nl');
		}


	}
	// 사고시 대공정 || 사고시 작업 내용
	if( nCell == 29 || nCell == 30  ) {	

		var gbn3 =  ds_list.GetColumn(nRow, "EVT_GBN3");

		if( gbn3 == "MR"){
			grd_list.SetCellProp('Body', '29' , 'ComboDataset', 'ds_evt_gbn51');
			grd_list.SetCellProp('Body', '30' , 'ComboDataset', 'ds_evt_gbn61');
		} else if( gbn3 == "MRL"){
			grd_list.SetCellProp('Body', '29' , 'ComboDataset', 'ds_evt_gbn52');
			grd_list.SetCellProp('Body', '30' , 'ComboDataset', 'ds_evt_gbn62');
		} else if( gbn3 == "레일 재사용"){
			grd_list.SetCellProp('Body', '29' , 'ComboDataset', 'ds_evt_gbn53');
			grd_list.SetCellProp('Body', '30' , 'ComboDataset', 'ds_evt_gbn63');
		} else if( gbn3 == "레일철거 및 교체"){
			grd_list.SetCellProp('Body', '29' , 'ComboDataset', 'ds_evt_gbn54');
			grd_list.SetCellProp('Body', '30' , 'ComboDataset', 'ds_evt_gbn64');
		}
	}


}
