/********************************************************************************
* 기      능   : 삽입 
********************************************************************************/
function fn_input() {
	//alert("입력");

	var cnt = ds_org.rowcount;

	trace(" cnt => " + cnt ) ;

	if( cnt < 1 ) {
		gfn_showAlert("선택된 담당주체가 없습니다.");	
		return;
	}

	var val1 = '';
	var val2 = '';
	var val3 = '';
	var val4 = '';
	var val5 = '';

	if( cnt > 0 ) {
		val1 = ds_org.GetColumn(0, "DEPT_N");
	}
	if( cnt > 1 ) {
		val2 = ds_org.GetColumn(1, "DEPT_N");
	}
	if( cnt > 2 ) {
		val3 = ds_org.GetColumn(2, "DEPT_N");
	}
	if( cnt > 3 ) {
		val4 = ds_org.GetColumn(3, "DEPT_N");
	}


	 var arr = [ val1
	           , val2
	           , val3
	           , val4
	           ];

	close(arr);	


}

