/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	if(ds_head.rowcount > 0)
	{
		// 수신부서 이면서 기안부서 결재완료시 상신 버튼 활성화
		if(ds_head.GetColumn(0,"APSTAT") == "3")
		{
		    bt_report.Visible = true;
		    //bt_sendback.Visible = true;
		    //bt_sendback.Enable = true;

            // 수신부서 결재선 상신의 경우 수신 부서, 부서명 설정
		    ds_head.SetColumn(0,"APSTAT", "4");
			ds_head.SetColumn(0,"REDPT1", ds_draft.GetColumn(0,"DPT1"));
			ds_head.SetColumn(0,"REDPT1_N", ds_draft.GetColumn(0,"DPT1_N"));
		    ds_head.SetColumn(0,"REIRUM", G_USER_NAME);
		}
		else if(ds_head.GetColumn(0,"APSTAT") == "2" || ds_head.GetColumn(0,"APSTAT") == "5") // 기안/결재 반송시에는 삭제 가능
		{
		    bt_delete.Enable = true;
		}

        disableFile();

        // 수신부서 결재선 상신의 경우 수신 부서, 부서명 설정

	    // if (ds_head.GetColumn(0,"APSTAT") = "3" && ds_head.GetColumn(0,"REDPT1") == G_DPT1)
	    // {
		    // ds_head.SetColumn(0,"APSTAT", "4");
			// ds_head.SetColumn(0,"REDPT1", ds_draft.GetColumn(0,"DPT1"));
			// ds_head.SetColumn(0,"REDPT1_N", ds_draft.GetColumn(0,"DPT1_N"));
		    // ds_head.SetColumn(0,"REIRUM", G_USER_NAME);

	    // } 
	    // else  // 기안부서 결재선 조회
	    // {
			// ds_cond.SetColumn(0,"APPGUBN","S");
	    // }

	} 
	else
	{
		fn_query3();

		// 결재선 정보가 없는 경우 상신버튼 활성화
		bt_report.Visible = true;

		// 결재선 정보가 없는 경우 최초 기안자의 정보를 입력한다.	
		ds_head.ClearData();
		ds_head.AddRow();

		ds_head.SetColumn(0,"DOCNO", ed_docno.text);
		ds_head.SetColumn(0,"DOCKIND", cb_dockind.Value);
		ds_head.SetColumn(0,"CRDPT1", ds_draft.GetColumn(0,"DPT1"));
		ds_head.SetColumn(0,"CRDPT1_N", ds_draft.GetColumn(0,"DPT1_N"));
		ds_head.SetColumn(0,"CRIRUM", G_USER_NAME);
/*  2014.04.01 이주형 수정(ds_dockind 선택된값 가져오기) */
//		ds_head.SetColumn(0,"REDPT1", ds_dockind.GetColumn(ds_dockind.currow,"DTEXT1"));
		ds_head.SetColumn(0,"REDPT1", ds_dockind.GetColumn(ds_dockind.FindRow("CODE",fa_dockind),"DTEXT1"));
		ds_head.SetColumn(0,"REDPT1_N", ds_tree.GetColumn(ds_tree.FindRow("DPT1",ds_head.GetColumn(0,"REDPT1")),"DPT1_N"));
	    //===============================================전결규정 저장으로 수정===============================================
	    if (fa_dockind == '3' || fa_dockind = '4') // 유상견적, 유상계약 에만 적용
	    {
			ds_head.SetColumn(0,"DISCRULE", fa_discrule);
		}
		else
		{
			ds_head.SetColumn(0,"DISCRULE", '');
		}
	    //====================================================================================================================
		enableFile();
	}
	//  수신 부서결재선 조회
	ds_cond.SetColumn(0,"APPGUBN",fa_appgubn);

	// 결재정보가 있는 경우 결재선 조회 
	fn_query2();
}

