function tr_org_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	var nRow = ds_tree.row;
	var rtn = tr_org.IsExpanded(nRow);
	if(nChar == 13){
		if(rtn){
			tr_org.ExpandItem(nRow, false);
		}else{
			tr_org.ExpandItem(nRow, true);
		}
	}
}

