function fn_OnLoadCompleted(obj) {
	gfn_initForm();

	cal_edudat.Value = gfn_getCurrDate();
	ed_edunam.Visible = false;

	// 코드 DataSet 가져오기
    var codeArr = ["EDU"];
    var dsObjArr = [ds_edu2];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);

	userGroup = gfn_getUserGroup();

	ds_cond.SetColumn(0,"USER_GRUP",userGroup);
	ds_cond.SetColumn(0,"EDUTIM_FM",'');

	tit_clearActionInfo();
	tit_addSearchActionInfo("wb01:SEOLCHI_VEND_S2");
	tit_addSearchActionInfo("wb05:WB0506001A_S4");	// 설치본부 권한 확인.
	//tit_addSearchActionInfo("ps02:PS0205001A_S8");      //공정 코드

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_zzlifnr2=ds_lifnr_cd ds_auth=ds_auth"  // ds_pronr=ds_pronr"
		, ""
		, "fn_afterQueryC"
		, ""
		, ""
		, true);

	fn_query();
}

