/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	//  자료가 없으면 리턴
	if (ds_list3.rowcount < 1) {
		gfn_showAlert("CW00010");
		return;
	}

	ds_list3.fireevent = false;

	gfn_copyDs(ds_list3, ds_list4, "CHECK=1");

	if (ds_list4.rowcount < 1) {
		gfn_showAlert("CW00180","체크표시");
		ds_list3.fireevent = true;
		return;
	}

	var cnt = ds_list4.GetRowCount();
	var cnt2;

	if( cnt > 0 )
	{
		for( var i = 0; i < ds_list4.GetRowCount(); i++ ){

			if ( ds_list4.GetColumn(i,"LIFNR") == "" &&
			     ds_list4.GetColumn(i,"LIFNR2") == "" ) 
			{
				gfn_showAlert("CW00001","업체명");
				ds_list3.fireevent = true;
				return;
			}
			if ( ds_list4.GetColumn(i,"RANK") == "" )
			{
				gfn_showAlert("CW00001","직급");
				ds_list3.fireevent = true;
				return;
			}
			if ( ds_list4.GetColumn(i,"NAME") == "" )
			{
				gfn_showAlert("CW00001","이름");
				ds_list3.fireevent = true;
				return;
			}
			if ( ds_list4.GetColumn(i,"TELH") == "" )
			{
				gfn_showAlert("CW00001","연락처");
				ds_list3.fireevent = true;
				return;
			}
			if ( length(ds_list4.GetColumn(i,"BIRTH")) == "" )
			{
				gfn_showAlert("CW00001","생년월일");
				ds_list3.fireevent = true;
				return;
			}
			if ( length(ds_list4.GetColumn(i,"BIRTH")) != 8)
			{
				gfn_showAlert("CW00011","8");
				ds_list3.fireevent = true;
				return;
			}
			if ( ds_list4.GetColumn(i,"LIFNR") != "" && length(ds_list4.GetColumn(i,"LIFNR2")) < 1 ) 
			{
				cnt2 = ds_zzlifnr2.searchRow("CODE== " + quote(ds_list4.GetColumn(i,"LIFNR")));
				ds_list4.SetColumn(i,"LIFNR2",ds_zzlifnr2.GetColumn(cnt2,"CODE_NAME"));
			}

			ds_list4.SetColumn(i,"EDUDAT",ds_head.GetColumn(0,"EDUDAT"));
			ds_list4.SetColumn(i,"EDUGBN",ds_head.GetColumn(0,"EDUGBN"));
			ds_list4.SetColumn(i,"BUMUN",ds_head.GetColumn(0,"BUMUN"));
			ds_list4.SetColumn(i,"EDUNAM",ds_head.GetColumn(0,"EDUNAM"));
			ds_list4.SetColumn(i,"POSID",ds_head.GetColumn(0,"POSID"));
			ds_list4.SetColumn(i,"EDUTIM_FM",ds_head.GetColumn(0,"EDUTIM_FM"));
			ds_list4.SetColumn(i,"EDUTIM_TO",ds_head.GetColumn(0,"EDUTIM_TO"));
		}
	}

	ds_list3.fireevent = true;

	tit_clearActionInfo();
	tit_addSelectExActionInfo(
		"wb05:WB0506001B_S2"
	   ,"wb05:WB0506001B_I1"
	   ,"wb05:WB0506001B_U1");

	// 저장 처리 
	tit_callService(
		""
		, ""
		, "ds_list4=ds_list4:A"
		, ""
		, ""
		, "fn_afterSave");
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
   	if ( ds_list.rowCount() > 0 ) {
		gfn_showMsg("CI00002", ds_list.rowCount() + "");
		gfn_copyDs(ds_list3, ds_ps1904,);
	} else {
		gfn_showMsg("CW00010");
	}

	if(length(fa_eduno) > 1) {
		cal_edudat.Enable = false;
		cb_edugbn.Enable = false;
		//cb_bumun.Enable = false;
		//cb_edunam.Enable = false;
		//ed_posid.Enable = false;
		ed_edutim_fm.Enable = false;
		ed_edutim_to.Enable = false;
		btn_addedu.Enable = false;
	}
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		ds_list.fireevent = true;
	}

	close();
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

