//=========================================================================================
// [ PART 5 ]
// Detail Process Event 정의 ( User Define function )
// 사용자 정의 함수는 ufn_xxxx() 형태로 작성 
//=========================================================================================
function ds_list3_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnId == "LIFNR_NM") // 작업업체명
	{

		if(length(Trim(varNewValue)) == 0)
		{
			ds_list3.SetColumn(nRow, "LIFNR", "");
			ds_list3.SetColumn(nRow, "LIFNR_NM", "");
			return;
		}

		// 존재하는 사업자번호 체크 (미존재시 팝업)
		nFindRow = ufn_chkLifnr(varNewValue);

		if(nFindRow <= 0)	// 사업자 번호 미존재시 팝업을 띄워준다.
		{
			var arg = "";
			//var arr = gfn_openPopup("WB0506001E", true, arg);
			var arr = gfn_openPopupUrl("WB05::WB0506001E.xml", true, arg);

			if(Length(arr) > 0)
			{
				ds_list3.SetColumn(nRow, "LIFNR", arr[0]);
				ds_list3.SetColumn(nRow, "LIFNR_NM", arr[1]);
			}

		}
		else
		{
			if(nFindRow == 1)
			{
				ds_list3.SetColumn(nRow, "LIFNR", ds_lifnr_grid.GetColumn(0, "CODE"));
				ds_list3.SetColumn(nRow, "LIFNR_NM", ds_lifnr_grid.GetColumn(0, "CODE_NAME"));
			}
			else
			{
				var arg = "F_LIFNR_N="+quote(varNewValue);
				//var arr = gfn_openPopup("WB0506001E", true, arg);
				var arr = gfn_openPopupUrl("WB05::WB0506001E.xml", true, arg);

				if(Length(arr) > 0)
				{
					ds_list3.SetColumn(nRow, "LIFNR", arr[0]);
					ds_list3.SetColumn(nRow, "LIFNR_NM", arr[1]);

				}
			}

		}


	}
}

function fn_afterQueryC()
{	
	ds_zzlifnr2.filter("LOEVM!='X'" );
	gfn_insertEmptyRow( ds_zzlifnr2, ,""," " );

	if(length(fa_edudat) > 1 )
	{
		ds_head.SetColumn(0,"EDUDAT",fa_edudat);
		cal_edudat.Value = fa_edudat;
	}
	if(length(fa_edugbn) > 0)
	{
		ds_head.SetColumn(0,"EDUGBN",fa_edugbn);
		cb_edugbn.Value = fa_edugbn;
	}
	if(length(fa_bumun) > 1)
	{
		ds_head.SetColumn(0,"BUMUN",fa_bumun);
		cb_bumun.Value = fa_bumun;
	}
	if(length(fa_edunam) > 1)
	{
		ds_head.SetColumn(0,"EDUNAM",fa_edunam);
		cb_edunam.Value = fa_edunam;
	}
	if(length(fa_eduno) > 1)
	{
		ds_head.SetColumn(0,"EDUNO",fa_eduno);
	}
	if(length(fa_posid) > 1)
	{
		ds_head.SetColumn(0,"POSID",fa_posid);
	}
	if(length(fa_edutim_fm) > 1)
	{
		ds_head.SetColumn(0,"EDUTIM_FM",fa_edutim_fm);
	}
	if(length(fa_edutim_to) > 1)
	{
		ds_head.SetColumn(0,"EDUTIM_TO",fa_edutim_to);
	}

	// 설치본부 권한일 경우.
	if(ds_auth.GetRowCount() > 0)
	{
		ds_kind2.Filter("CODE == '' || CODE == '6' || CODE == '7' || CODE == '8'");
	}
	else
	{
		ds_kind2.Filter("CODE == '' || CODE == '1' || CODE == '2' || CODE == '3' || CODE == '4' || CODE == '5'");
	}
}

function grd_list2_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}

function btn_addedu_OnClick(obj)
{
	var cnt = ds_list3.GetRowCount();

	// 필수 입력 항목 체크
	if( ds_head.GetColumn(0,"EDUGBN") == "1" || ds_head.GetColumn(0,"EDUGBN") == "2")
	{
		gfn_showAlert("CW00022", "신규자교육과 수시교육은 모바일에서만 입력가능합니다. 교육구분란" );
		return;
	}
	if( ds_head.GetColumn(0,"EDUDAT") == "" )
	{
		gfn_showAlert("CW00001", " 교육일 " );
		return;
	}
	if( ds_head.GetColumn(0,"EDUGBN") == "" )
	{
		gfn_showAlert("CW00001", " 교육구분 " );
		return;
	}
	if( ds_head.GetColumn(0,"BUMUN") == "" )
	{
		gfn_showAlert("CW00001", " 사업부문 " );
		return;
	}
	if( ds_head.GetColumn(0,"EDUNAM") == null || ds_head.GetColumn(0,"EDUNAM") == "" )
	{
		gfn_showAlert("CW00001", " 교육 실시자 " );
		return;
	}
	if( ds_head.GetColumn(0,"EDUTIM_FM") == "" )
	{
		gfn_showAlert("CW00001", " 교육시작시간" );
		return;
	}
	if( ds_head.GetColumn(0,"EDUTIM_TO") == "" )
	{
		gfn_showAlert("CW00001", " 교육종료시간" );
		return;
	}
	if(ds_head.GetColumn(0,"EDUTIM_FM") > ds_head.GetColumn(0,"EDUTIM_TO"))
	{
		gfn_showAlert("CW00114","교육종료시간이 시작시간 보다 큰 건");
		return;
	}
	if(ds_head.GetColumn(0,"EDUTIM_FM") > ds_head.GetColumn(0,"EDUTIM_TO"))
	{
		gfn_showAlert("CW00114","교육종료시간이 시작시간 보다 큰 건");
		return;
	}
	if(ds_head.GetColumn(0,"EDUTIM_FM") > 2400 || ds_head.GetColumn(0,"EDUTIM_TO") > 2400 )
	{
		gfn_showAlert("CW00114","교육시간 입력이");
		return;
	}
	if(length(ds_head.GetColumn(0,"EDUTIM_FM")) <> 4 || length(ds_head.GetColumn(0,"EDUTIM_TO")) <> 4 )
	{
		gfn_showAlert("CW00011","4");
		return;
	}

	ds_list3.AddRow();
	ds_list3.RowPos = cnt;
	ds_list3.SetColumn(ds_list3.row,"CHECK","1");
}

var col = "";
function grd_list2_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	if(nCell == 1)	//	작업업체
	{
		//var arg = ds_list.GetColumn(nRow, "WRKLFN");
		var arg = "";
		//var arr = gfn_openPopup("WB0506001E", true, arg);
		var arr = gfn_openPopupUrl("WB05::WB0506001E.xml", true, arg);

		if(Length(arr) > 0)
		{
			//ds_list.fireevent = false;
			ds_list3.SetColumn(nRow, "LIFNR", arr[0]);
			ds_list3.SetColumn(nRow, "LIFNR_NM", arr[1]);
			//ds_list.fireevent = true;
		}

	}

	if(nCell = 5)
	{
		var rect = obj.GetCellRect(nRow, ncell);
		cal_temp.top = rect[1] +60 ;
		cal_temp.Left = rect[0];
		cal_temp.Visible = true;

		switch( nCell )
		{
			case 5:
				cal_temp.Value = ds_list3.GetColumn(nRow, "BIRTH");
				col = "BIRTH";
			break;
		}

		cal_temp.SetFocus();
	}

	if(nCell = 7) 
	{
		var hogin2 = gfn_openPopup("MM0901004A", true);

		if (hogin2 != null || length(hogin2) > 1)
		{
			 ds_list3.SetColumn(nRow,"POSID",hogin2[4]);
		}
	}
}

function cal_temp_OnDayClick(obj,strText)
{
	gfn_clearMsgbar();

	ds_list3.setColumn(ds_list3.row, col, strText);

	obj.Visible = false;
}

function cal_temp_OnKillFocus(obj)
{
	obj.Visible = false;
}

function btn_hogi_OnClick(obj)
{
	var hogin = gfn_openPopup("MM0901004A", true);

	if (hogin != null && obj == btn_hogi)
	{
		ed_posid.value = hogin[4];
	}
}

function ds_head_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID = "EDUGBN")
	{
		if(varNewValue == "1")
		{
			ed_posid.Enable = false;
		} else {
			ed_posid.Enable = true;
		}

		/* 리모델링 교육과 기존 안전교육 구분. */
		if(varNewValue == '6' || varNewValue == '7' || varNewValue == '8')
		{
			ed_edunam.Visible = true;
			cb_edunam.Visible = false;
			ds_head.SetColumn(0, "BUMUN", '승강기');
		}
		else
		{
			ed_edunam.Visible = false;
			cb_edunam.Visible = true;
			ds_head.SetColumn(0, "BUMUN", '');
		}
	}
}

function ufn_chkLifnr(lifnr)
{
	var rValue = 0;

	ds_condLifnr.ClearData();
	ds_condLifnr.addRow();
	ds_condLifnr.SetColumn(0, "LIFNR_N", lifnr);

	tit_clearActionInfo();
	tit_addSearchActionInfo("qm05:QM0505001B_S1");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_condLifnr"
		, "ds_lifnr_grid=ds_list"
		, ""
		, "" // fn_afterQueryC
		, ""
		, ""
		, true);


	if(ds_lifnr_grid.GetRowCount() == 0)
	{
		rValue = -1;
	}
	else
	{
		rValue = ds_lifnr_grid.GetRowCount();
	}

	return rValue;
}
