function fn_OnLoadCompleted(obj) 
{
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);	

	cal_date_to.value = gfn_getCurrDate();
	cal_date_fm.value = gfn_getBeforeDate(cal_date_to.value, 15);
	ds_cond.SetColumn(0,"ZZGUBUN", "L");
	gfn_showMsg("CI00007");

	if(g_user_id != "2011662" && g_user_id != "2025558" && g_user_id != "2023709" &&
	   g_user_id != "2014211" && g_user_id != "2021242" && g_user_id != "2029987" && g_user_id != '2029847')
	{
		ufn_enableBtn("delete", false);
	} else {
		ufn_enableBtn("delete", true);
	}

	userGroup = gfn_getUserGroup();

	ds_cond.SetColumn(0,"USER_GRUP",userGroup);


	tit_clearActionInfo();
	tit_addSearchActionInfo("wb01:SEOLCHI_VEND_S2");
	tit_addSearchActionInfo("wb05:WB0501002A_S2");      // 안전 권한

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_zzlifnr=ds_lifnr_cd ds_auth=ds_auth"  // ds_pronr=ds_pronr"
		, ""
		, "fn_afterQueryC"
		, ""
		, ""
		, true);
}

