/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() 
{

    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	// 필수 입력 항목 체크 
	if ( gfn_isNull(cal_date_fm, true) ) {
		gfn_showMsg("CW00001",st_gubn.Text+"(시작)");
		return;
	}
	if ( gfn_isNull(cal_date_to, true) ) {
		gfn_showMsg("CW00001",st_gubn.Text+"(종료)");
		return;
	}

	tit_clearActionInfo();	
	tit_addSearchActionInfo("wb05:WB0501002A_S1");  	
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"  	
        , ""
        , "fn_afterQuery"  
        , ""
        , ""          
        , true);


}

