/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete()
{
	if ( ds_list.rowCount() < 1 ) 
	{
		gfn_showMsg("CW00013");
		return;
	}

	gfn_copyDs(ds_list, ds_list2, "CHECK=1");

	if ( !gfn_showConfirm("CI00010") ) {
		return;
	}

	tit_clearActionInfo();
	tit_addMultiActionInfo("wb05:WB0501001A_D1");
	tit_addMultiActionInfo("wb05:WB0501001A_D2");

	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2"
		, ""
		, ""
		, "fn_afterDelete");

}
