	gfn_exportExcel(grd_list, true);
}
/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel2() 
{

	if( ds_list.rowcount > 0 )
	{
		var str = "현장안전 패트롤 점검 현황 : "   + ds_cond.GetColumn(0, "DATE_FM")  + " ~ "	+ ds_cond.GetColumn(0, "DATE_TO");

		setExcelGridHeader(grd_list,str);
	}
}

function setExcelGridHeader(grdObj,strText)
{

	create("grid","grdExcel",'BindDataSet="' + grdObj.BindDataset);

	grdExcel.contents = grdObj.contents;

	//Trace(grdExcel.contents);
	var headCnt = getHeadCount(grdExcel);
	var totCol  = grdExcel.GetColCount();

	//Trace(headCnt);
	var irow;

	for(var i = 0 ; i < headCnt ; i++ )
	{
		irow = toInteger(grdExcel.GetCellProp("head",i,"row"));
		grdExcel.SetCellProp("head",i,"row",irow+1);
	}

	var str_temp = grdExcel.GetCurContents();
	var str_Head = '<cell align="left" col="0" colspan="' + totCol + '" display="text" text="' + strText + '"/>';	
	str_temp = replace(str_temp, '</head>',str_Head +'</head>');
	//str_temp = replace(str_temp, '<head>', '<head>'+str_Head);

	grdExcel.contents = str_temp;

	grdExcel.ExportExcelEx("승강기","A1",false,true,true);

	Destroy(grdExcel);
}

function getHeadCount(grdObj)
{
	var strConts = grdObj.Contents;
	var iPos = 0;
	var strHead = 0;


	iPos = pos(strConts,"<head>");

	strHead = Mid(strConts,iPos);

	iPos = pos(strHead,"</head>");

	strHead = Mid(strHead,0,iPos+7);


	//trace(strHead);

	var spObj = split(strHead,chr(10),"webstyle");

	//Trace(spObj);
	return spObj.length()-2;
}


function btn_popProj_OnClick(obj)
{
	var posid = null;

	var arg = "pjt="+quote(ed_posid.Text);
		arg += " rep=NH";

	posid = gfn_openPopup("PS9602001A", true, arg);

	if (posid != null){
		ed_posid.value = posid[0];
    }
}

function grd_list_onCellDBLClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (nCell == 2){
		// 안전점검 CHECKLIST 조회
		var arg = "fa_posid=" + quote(obj.GetCellValue(nRow,7));
		arg += " fa_chkdat=" + quote(obj.GetCellValue(nRow,2));
		arg += " fa_zzgubun=" + quote('L');
		arg += " fa_chper=" + quote(obj.GetCellValue(nRow,10));

		gfn_openWin("WB0501001A", arg);
	}
}

function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 || nCell == 1) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}

}

function fn_afterDelete(errCode, errMsg) {
	if ( errCode != 0 )
	{
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
	}

	fn_query();
}

