function fn_OnLoadCompleted(obj) {
	//SetTimer(1000, 300000); //5분마다 LCD 체크
//	GFN_InitForm(this);
	tit_CreateActionInfo();
	ds_list.Reset();
	ds_list2.Reset();

	var codeArr = ["020000","030000","19000","20000","22000"];
	var dsObjArr = [ds_cause,ds_result,ds_reason,ds_rescue,ds_pst];
	var addTypeArr = [G_CODE_SELECT,G_CODE_SELECT,G_CODE_NONE,G_CODE_NONE,G_CODE_NONE];

	gfn_SearchCommCode2(codeArr, dsObjArr, addTypeArr);
	fn_getPart1();
	fn_getPart2();

	cal_arrive.Value = gfn_getCurrDate();
	cal_finish.Value = gfn_getCurrDate();

	ed_arrdate.Text = substr(ARRG_DATE,0,4) + "-" + substr(ARRG_DATE,4,2) + "-" + substr(ARRG_DATE,6,2);
	ed_arrtime.Text = substr(ARRG_TIME,0,2) + ":" + substr(ARRG_TIME,2,2) + ":" + substr(ARRG_TIME,4,2);

	if(CONSL_MCD == "010100") { //구출
		cal_lock.Value = LOCK_DATE;
		cb_hh3.Value = substr(LOCK_TIME,0,2);
		cb_mm3.Value = substr(LOCK_TIME,2,2);
		cal_rescue.Value = LOCK_DATE;
	}
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_cause.Index = 0;
	cb_result.Index = 0;

	ds_reason.InsertRow();
	ds_reason.SetColumn(0, "CODE", "");
	ds_reason.SetColumn(0, "CODE_NAME", "-- 없음 --");
	cb_reason.Index = 0;

	ds_rescue.InsertRow();
	ds_rescue.SetColumn(0, "CODE", "");
	ds_rescue.SetColumn(0, "CODE_NAME", "-- 없음 --");
	cb_rescue.Index = 0;

	ds_pst.InsertRow();
	ds_pst.SetColumn(0, "CODE", "");
	ds_pst.SetColumn(0, "CODE_NAME", "-- 없음 --");
	cb_pst.Index = 0;

	cb_hh1.Index = 0;
	cb_hh2.Index = 0;
	cb_hh3.Index = 0;
	cb_hh4.Index = 0;

	cb_mm1.Index = 0;
	cb_mm2.Index = 0;
	cb_mm3.Index = 0;
	cb_mm4.Index = 0;

	ed_arrdate.SetFocus();
	//btn_cancel.SetFocus();

	if(TPST == "21006") { //미처리
		cal_arrive.Enable = false;
		cb_hh1.Enable = false;
		cb_mm1.Enable = false;
		cb_part1.Enable = false;
		cb_part2.Enable = false;
		cb_cause.Enable = false;
		//cb_result.Enable = false;
		cb_reason.Enable = false;
		cb_rescue.Enable = false;
		cal_lock.Enable = false;
		cb_hh3.Enable = false;
		cb_mm3.Enable = false;
		cal_rescue.Enable = false;
		cb_hh4.Enable = false;
		cb_mm4.Enable = false;
		ed_name.Enable = false;
		ed_tel.Enable = false;
		cb_pst.Enable = false;
	}

	if(TPST == "21006") {
		fn_Query();
	}
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_Query(obj) {
	ds_cond.SetColumn(0, "ACPT_NO", ACPT_NO);

	tit_clearActionInfo();
	tit_addSearchActionInfo("pcc01:PCC0101006A_S3");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list2=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
function fn_afterQuery(errCode, errMsg) {
	cal_arrive.Value = ds_list2.GetColumn(0, "ARRV_DATE");
	cb_hh1.Value = ds_list2.GetColumn(0, "AH");
	cb_mm1.Value = ds_list2.GetColumn(0, "AM");
	cb_part1.Value = ds_list2.GetColumn(0, "BKDN_LOC_LCD");
	cb_part2.Value = ds_list2.GetColumn(0, "BKDN_LOC_CD");
	cb_cause.Value = ds_list2.GetColumn(0, "BKDN_CAU_CD");
	cb_reason.Value = ds_list2.GetColumn(0, "RSN_CD");
	cb_rescue.Value = ds_list2.GetColumn(0, "RESC_MN_CD");
	cal_lock.Value = ds_list2.GetColumn(0, "LOCK_DATE");
	cb_hh3.Value = ds_list2.GetColumn(0, "LH");
	cb_mm3.Value = ds_list2.GetColumn(0, "LM");
	cal_rescue.Value = ds_list2.GetColumn(0, "RESC_DATE");
	cb_hh4.Value = ds_list2.GetColumn(0, "RH");
	cb_mm4.Value = ds_list2.GetColumn(0, "RM");
	ed_name.Text = ds_list2.GetColumn(0, "MEMO");
}

//=========================================================================================
// [ PART 5 ]
// Detail Process Event 정의 ( User Define function )
// 사용자 정의 함수는 ufn_xxxx() 형태로 작성 
//=========================================================================================
function fn_getPart1() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("pcc01:PCC0101006A_S1");
	tit_callService(
        ""
        , ""
        , ""
        , "ds_part1=ds_list"
        , ""
        , "fn_afterPart1"
        , true);
}

function fn_getPart2() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("pcc01:PCC0101006A_S2");
	tit_callService(
        ""
        , ""
        , ""
        , "ds_part2=ds_list"
        , ""
        , "fn_afterPart2"
        , true);
}

function fn_afterPart1(errCode, errMsg) {
	ds_part1.InsertRow();
	ds_part1.SetColumn(0, "CODE", "");
	ds_part1.SetColumn(0, "CODE_NAME", "-- 선택 --");
	cb_part1.Index = 0;
}

function fn_afterPart2(errCode, errMsg) {
	ds_part2.filter("GROUP_CODE == ''");

	ds_part2.InsertRow();
	ds_part2.SetColumn(0, "CODE", "");
	ds_part2.SetColumn(0, "CODE_NAME", "-- 선택 --");
	cb_part2.Index = 0;
}

