function btn_Exit_OnClick(obj) {
	Close();
}

function btn_confirm_OnClick(obj) {
/*
	if(parseInt(substr(cal_arrive.Value,10,2)) > 24 || parseInt(substr(cal_arrive.Value,10,2)) == 0) {
		GFN_ShowAlert(this, "도착시간 입력이 잘못되었습니다.");
		return;
	}

	if(parseInt(substr(cal_finish.Value,10,2)) > 24 || parseInt(substr(cal_finish.Value,10,2)) == 0) {
		GFN_ShowAlert(this, "완료시간 입력이 잘못되었습니다.");
		return;
	}
*/
	var t_arrg = "";
	var t_arri = "";
	var t_fini = "";

	t_arrg = ARRG_DATE + ARRG_TIME + "";
	t_arri = cal_arrive.Value + cb_hh1.Value + cb_mm1.Value + "00";
	t_fini = cal_finish.Value + cb_hh2.Value + cb_mm2.Value + "00";

	if(t_arrg >= t_arri) {
		GFN_ShowAlert(this, "도착시간 입력이 잘못되었습니다.");
		return;
	}

	if(t_arrg >= t_fini || t_arri >= t_fini) {
		GFN_ShowAlert(this, "완료시간 입력이 잘못되었습니다.");
		return;
	}

	if(GFN_IsNull(cal_arrive.Value)) {
		GFN_ShowAlert(this, "CW00012", st_arrive.Text);
		return;
	}

	if(GFN_IsNull(cb_hh1.Value)) {
		GFN_ShowAlert(this, "CW00012", st_hh1.Text);
		return;
	}

	if(GFN_IsNull(cb_mm1.Value)) {
		GFN_ShowAlert(this, "CW00012", st_mm1.Text);
		return;
	}

	if(cb_reason.Value == "" || TPST == "21006") { //미처리외
		if(GFN_IsNull(cal_finish.Value)) {
			GFN_ShowAlert(this, "CW00012", st_finish.Text);
			return;
		}

		if(GFN_IsNull(cb_hh2.Value)) {
			GFN_ShowAlert(this, "CW00012", st_hh2.Text);
			return;
		}

		if(GFN_IsNull(cb_mm2.Value)) {
			GFN_ShowAlert(this, "CW00012", st_mm2.Text);
			return;
		}

		if(GFN_IsNull(cb_result.Value)) {
			GFN_ShowAlert(this, "CW00012", st_result.Text);
			return;
		}
	}

	if(GFN_IsNull(cb_part1.Value)) {
		GFN_ShowAlert(this, "CW00012", st_part1.Text);
		return;
	}

	if(TPST <> "21006" && GFN_IsNull(cb_part2.Value)) {
		GFN_ShowAlert(this, "CW00012", st_part2.Text);
		return;
	}

	if(GFN_IsNull(cb_cause.Value)) {
		GFN_ShowAlert(this, "CW00012", st_cause.Text);
		return;
	}

	if(CONSL_MCD == "010100") { //갇힘건일 경우(구출자 정보 등록)
		if(GFN_IsNull(cb_rescue.Value)) {
			GFN_ShowAlert(this, "CW00012", st_rescue.Text);
			return;
		}
/*
		if(parseInt(substr(cal_lock.Value,10,2)) > 24 || parseInt(substr(cal_lock.Value,10,2)) == 0) {
			GFN_ShowAlert(this, "갇힘일시 입력이 잘못되었습니다.");
			return;
		}

		if(parseInt(substr(cal_rescue.Value,10,2)) > 24 || parseInt(substr(cal_rescue.Value,10,2)) == 0) {
			GFN_ShowAlert(this, "구출일시 입력이 잘못되었습니다.");
			return;
		}
*/
		if(GFN_IsNull(cal_lock.Value)) {
			GFN_ShowAlert(this, "CW00012", st_lock.Text);
			return;
		}

		if(GFN_IsNull(cb_hh3.Value)) {
			GFN_ShowAlert(this, "CW00012", st_hh3.Text);
			return;
		}

		if(GFN_IsNull(cb_mm3.Value)) {
			GFN_ShowAlert(this, "CW00012", st_mm3.Text);
			return;
		}

		if(GFN_IsNull(cal_rescue.Value)) {
			GFN_ShowAlert(this, "CW00012", st_rescue_date.Text);
			return;
		}

		if(GFN_IsNull(cb_hh4.Value)) {
			GFN_ShowAlert(this, "CW00012", st_hh4.Text);
			return;
		}

		if(GFN_IsNull(cb_mm4.Value)) {
			GFN_ShowAlert(this, "CW00012", st_mm4.Text);
			return;
		}
	}

	var t_gps_x = 0;
	var t_gps_y = 0;
	t_gps_x = getReg("LOCATION_X");
	t_gps_y = getReg("LOCATION_Y");

	if(t_gps_x == "") {
		t_gps_x = 0;
	}

	if(t_gps_y == "") {
		t_gps_y = 0;
	}

	ds_list.Reset();
	ds_list.SetColumn(0, "ACPT_NO", ACPT_NO);
	ds_list.SetColumn(0, "ACPT_DATE", ACPT_DATE);
	ds_list.SetColumn(0, "CONSL_LCD", CONSL_LCD);
	ds_list.SetColumn(0, "UPN", UPN);
	ds_list.SetColumn(0, "CST", CST);
	ds_list.SetColumn(0, "PJT", PJT);
	ds_list.SetColumn(0, "HNO", HNO);
	ds_list.SetColumn(0, "CONSL_NO", CONSL_NO);
	ds_list.SetColumn(0, "ACPT_USER_ID", ACPT_USER_ID);
	ds_list.SetColumn(0, "CUST_NO", CUST_NO);
	ds_list.SetColumn(0, "CUST_NM", CUST_NM);
	ds_list.SetColumn(0, "CALL_TNO", CALL_TNO);
	ds_list.SetColumn(0, "ETC_1", ETC_1);
	ds_list.SetColumn(0, "ETC_2", ETC_2);
	ds_list.SetColumn(0, "ARRIVE_DATE", cal_arrive.Value);
	ds_list.SetColumn(0, "ARRIVE_TIME", cb_hh1.Value + cb_mm1.Value + "00");
	ds_list.SetColumn(0, "FINISH_DATE", cal_finish.Value);
	ds_list.SetColumn(0, "FINISH_TIME", cb_hh2.Value + cb_mm2.Value + "00");
	ds_list.SetColumn(0, "PART1", cb_part1.Value);
	ds_list.SetColumn(0, "PART2", cb_part2.Value);
	ds_list.SetColumn(0, "CAUSE", cb_cause.Value);
	ds_list.SetColumn(0, "RESULT", cb_result.Value);
	ds_list.SetColumn(0, "GPS_X", t_gps_x);
	ds_list.SetColumn(0, "GPS_Y", t_gps_y);

	if(CONSL_MCD == "010100") { //구출
		ds_list.SetColumn(0, "REASON", "");
		ds_list.SetColumn(0, "PST3", "21005");
	} else if(cb_reason.Value > "") { //미처리
		ds_list.SetColumn(0, "REASON", cb_reason.Value);
		ds_list.SetColumn(0, "PST3", "21006");
		ds_list.SetColumn(0, "FINISH_DATE", "");
		ds_list.SetColumn(0, "FINISH_TIME", "");
	} else { //완료
		ds_list.SetColumn(0, "REASON", "");
		ds_list.SetColumn(0, "PST3", "21004");
	}

	if(cb_reason.Value > "") { //미처리
		ds_list.SetColumn(0, "REASON", cb_reason.Value);
		ds_list.SetColumn(0, "PST3", "21006");
		ds_list.SetColumn(0, "FINISH_DATE", "");
		ds_list.SetColumn(0, "FINISH_TIME", "");
	}

	if(CONSL_MCD == "010100") { //구출
		ds_list.SetColumn(0, "RESCUE", cb_rescue.Value);
		ds_list.SetColumn(0, "LOCK_DATE", cal_lock.Value);
		ds_list.SetColumn(0, "LOCK_TIME", cb_hh3.Value + cb_mm3.Value + "00");
		ds_list.SetColumn(0, "RESCUE_DATE", cal_rescue.Value);
		ds_list.SetColumn(0, "RESCUE_TIME", cb_hh4.Value + cb_mm4.Value + "00");
		ds_list.SetColumn(0, "NAME", ed_name.Text + "-" + ed_tel.Text + "-" + cb_pst.Text);
		ds_list.SetColumn(0, "TEL", ed_tel.Text);
		ds_list.SetColumn(0, "PST", cb_pst.Value);
	} else { //그외
		ds_list.SetColumn(0, "RESCUE", "");
		ds_list.SetColumn(0, "LOCK_DATE", "");
		ds_list.SetColumn(0, "LOCK_TIME", "");
		ds_list.SetColumn(0, "RESCUE_DATE", "");
		ds_list.SetColumn(0, "RESCUE_TIME", "");
		ds_list.SetColumn(0, "NAME", "");
		ds_list.SetColumn(0, "TEL", "");
		ds_list.SetColumn(0, "PST", "");
	}

	if(TPST == "21006") { //미처리
		ds_list.SetColumn(0, "FINISH_DATE", cal_finish.Value);
		ds_list.SetColumn(0, "FINISH_TIME", cb_hh2.Value + cb_mm2.Value + "00");
	}

	tit_clearActionInfo();

	if(TPST <> "21006") { //미처리외
		//고장접수 처리이력 UPDATE(UC_ASS_PROC_HIST)
		tit_addSaveActionInfo(
			""
			, "pcc01:PCC0101006A_U1"
			, ""
			, "");

		//호기기타정보 - 호기상태 UPDATE(UC_HO_ETC_INFO)
		tit_addSaveActionInfo(
			""
			, "pcc01:PCC0101006A_U2"
			, ""
			, "");

		//시스템 사용자 - 상태코드 UPDATE(UC_SYS_USER)
		tit_addSaveActionInfo(
			""
			, "pcc01:PCC0101006A_U3"
			, ""
			, "");

		//고장접수기본 - 갇힘일자, 시간 UPDATE(UC_ASS_BASE)
		tit_addSaveActionInfo(
			""
			, "pcc01:PCC0101006A_U4"
			, ""
			, "");

		//실적보고이력 UPDATE(UC_ASS_FIN_RPT)
		tit_addSaveActionInfo(
			""
			, "pcc01:PCC0101006A_U5"
			, ""
			, "");
	} else { //미처리
		//고장접수 처리이력 UPDATE(UC_ASS_PROC_HIST)
		tit_addSaveActionInfo(
			""
			, "pcc01:PCC0101006A_U7"
			, ""
			, "");

		//실적보고이력 UPDATE(UC_ASS_FIN_RPT)
		tit_addSaveActionInfo(
			""
			, "pcc01:PCC0101006A_U8"
			, ""
			, "");
	}

	/* 20100405 LJH 해피콜 입력 추가 */
	if(cb_reason.Value == "" || (TPST == "21006" && cb_result.Value > "")) { //미처리외
		//UC_CMP_대상_리스트 INSERT(UC_CMP_OBJ_LIST)
		tit_addSaveActionInfo(
			""
			, "pcc01:PCC0101006A_I3"
			, ""
			, "");

		//UC_CMP_대상_상세 INSERT(UC_CMP_OBJ_DTL)
		tit_addSaveActionInfo(
			""
			, "pcc01:PCC0101006A_I4"
			, ""
			, "");

		//UC_CMP_데이터 UPDATE(UC_CMP_DATA)
		tit_addSaveActionInfo(
			""
			, "pcc01:PCC0101006A_U6"
			, ""
			, "");
	}

	tit_callService(
        ""
        , ""
        , "ds_list=ds_list:A"
        , ""
        , ""
        , "fn_afterSave");
}

function fn_afterSave(errCode, errMsg) {
	//SetTimer(1000, 300000); //5분마다 LCD 체크

	if(errCode != 0) {
		GFN_showMsg(this, "CE00001");
	} else {
		GFN_showMsg(this, "CI00003");
		GV_FLAG = true; //부모창 조회 체크
		parent.fn_Query();
		Close();
	}
}

function cb_part1_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	ds_part2.filter("GROUP_CODE == " + quote(strCode));

	ds_part2.InsertRow();
	ds_part2.SetColumn(0, "CODE", "");
	ds_part2.SetColumn(0, "CODE_NAME", "-- 선택 --");
	cb_part2.Index = 0;
}

