query.statement:
MERGE INTO SAPHEE.ZFST0400 AS A USING ( SELECT 1 AS ORDR, MANDT, ADOC_NUMB, ADOC_NTIM, DRAF_STAT, ADIT_STAT, APPR_STAT FROM SAPHEE.ZFST0103 WHERE MANDT = #G_MANDT# AND ADOC_CLAS = CAST(#ADOC_CLAS# AS VARCHAR(60)) AND ADOC_NUMB = CAST(#ADOC_NUMB# AS VARCHAR(60)) AND ADOC_NTIM = CAST(COALESCE( NULLIF(TRIM(#ADOC_NTIM#||''), ''), '-1') AS INTEGER ) UNION SELECT 2, #G_MANDT# , CAST(#ADOC_NUMB# AS VARCHAR(60)), CAST(COALESCE( NULLIF(TRIM(#ADOC_NTIM#||''), ''), '1') AS INTEGER ), '110', '', '' FROM SYSIBM.SYSDUMMY1 ORDER BY 1 FETCH FIRST 1 ROW ONLY WITH UR ) AS B ON A.MANDT = A.MANDT -- 클라이언트 AND A.ORDR_NUMB = B.ADOC_NUMB -- 결재문서번호 WHEN MATCHED THEN UPDATE SET ORDR_STAT = CASE /* 발주폐기 */ WHEN A.ORDR_STAT NOT IN ( '410', '460' ) AND B.DRAF_STAT = '510' THEN '410'
/* 발주반려 */ WHEN A.ORDR_STAT NOT IN ( '420', '450', '460' ) AND ( B.ADIT_STAT = '040' OR B.APPR_STAT = '040' ) THEN '420'
/* 발주승인 */ WHEN A.ORDR_STAT NOT IN ( '410', '450', '460' ) AND ( B.DRAF_STAT = '150' OR B.ADIT_STAT = '250' OR B.APPR_STAT = '390') THEN '450'
/* 발주결재중 */ WHEN A.ORDR_STAT NOT IN ( '450', '460' ) AND B.DRAF_STAT = '190' AND B.ADIT_STAT NOT IN ( '040', '250' ) AND B.APPR_STAT NOT IN ( '040', '390' ) THEN '440'
/* 발주작성중 */ WHEN A.ORDR_STAT NOT IN ( '450', '460' ) AND B.DRAF_STAT NOT IN ( '150', '190' ) AND B.ADIT_STAT != '250' AND B.APPR_STAT != '390' THEN '430'
/* 기타 원본 유지 */ ELSE A.ORDR_STAT END
,ORDR_APDT = CASE /* 발주폐기 */ WHEN A.ORDR_STAT NOT IN ('410', '460' ) AND B.DRAF_STAT = '510' THEN HEX(CURRENT_DATE)
/* 발주반려 */ WHEN A.ORDR_STAT NOT IN ('420', '450', '460' ) AND ( B.ADIT_STAT = '040' OR B.APPR_STAT = '040' ) THEN '00000000'
/* 발주승인 */ WHEN A.ORDR_STAT NOT IN ( '450', '460' ) AND ( B.DRAF_STAT = '150' OR B.ADIT_STAT = '250' OR B.APPR_STAT = '390') THEN HEX(CURRENT_DATE)
/* 발주작성중 , 발주결재중, 기타 */ ELSE A.ORDR_APDT END
