query.statement:
MERGE INTO SAPHEE.ZWBGRUP AS A USING ( SELECT VALUE(MAX(MANDT),'') AS MANDT , VALUE(MAX(GNUMB),'') AS GNUMB , VALUE(MAX(GGRUP),'') AS GGRUP FROM SAPHEE.ZWBGRUP WHERE MANDT = #G_MANDT# AND GNUMB = #USERNUMB# AND GGRUP = 'ZCS31'
) AS B ON A.MANDT = B.MANDT AND A.GNUMB = B.GNUMB AND A.GGRUP = B.GGRUP WHEN MATCHED THEN UPDATE SET GDATE = SUBSTR(SAPHEE.TO_DATETIME(),1,8)
WHEN NOT MATCHED THEN INSERT VALUES ( #G_MANDT# , #USERNUMB# , 'ZCS31' , #G_USER_ID# , SUBSTR(SAPHEE.TO_DATETIME(),1,8) )
