query.statement:
SELECT WTGBTR*100 AS WTGBTR,MBGBTR,OWAER,MEINB,MATNR,PT_GBN,CPUDT,AUFNR, CASE WHEN BLTXT = '' OR BLTXT IS NULL THEN ( SELECT MAKTX FROM SAPHEE.MAKT WHERE MANDT = '183' AND MATNR = A.MATNR
) ELSE A.BLTXT
END AS BLTXT FROM ( SELECT WTGBTR, MBGBTR, OWAER, MEINB, BLTXT, MATNR, '' AS PT_GBN, SUBSTR(OBJNR,9,6) AS AUFNR, CPUDT FROM SAPHEE.COVP WHERE MANDT = '183' AND LEDNR = '00' AND OBJNR IN ( SELECT DISTINCT 'OR000000'||AUFNR AS AUFNR FROM SAPHEE.ZMMT117 WHERE MANDT = '183' AND PJTID = #PJTID# AND PJTXT LIKE '%'||#PJTXT#||'%' ) AND GJAHR BETWEEN '2011' AND YEAR(CURRENT DATE) AND VERSN = '000' AND WRTTP in (04,11) AND BUDAT BETWEEN '20110101' AND HEX(CURRENT DATE) AND MVFLG <> 'X' ORDER BY AUFNR, MATNR DESC ) AS A
