AND VKBUR = #{vkbur}
AND VKGRP = #{vkgrp}
AND ZKUNNR = #{zkunnr}
AND ZPRGFLG = #{zprgflg}
AND KUNNR = #{kunnr}
AND AUART = #{auart}
AND ZREGN = #{zregn}
AND ZAGNT = #{gno}
AND ZKUNNR like '%'|| #{darijumzkunnr} GROUP BY MANDT, QTNUM ) -- 2016.12.09 최종 차수 조회 여부 추가 by mj.Shin End ------
) E LEFT OUTER JOIN TABLE ( SELECT LIFNR AS KUNNR, NAME1 FROM SAPHEE.LFA1 WHERE MANDT = #{mandt} AND LIFNR = E.ZAGNT UNION SELECT KUNNR, NAME1 FROM SAPHEE.KNA1 WHERE MANDT = #{mandt} AND KTOKD = 'Z004' AND KUNNR = E.ZAGNT ) F ON E.ZAGNT = F.KUNNR ) C LEFT OUTER JOIN TABLE ( SELECT KUNNR, NAME1, NAME2 FROM SAPHEE.KNA1 WHERE MANDT = #{mandt} AND KUNNR = C.ZKUNNR ) D ON C.ZKUNNR = D.KUNNR ) A LEFT OUTER JOIN TABLE ( SELECT KUNNR, NAME1 AS BUYR_NM, LAND1 -- 거래선명 FROM SAPHEE.KNA1 WHERE MANDT = A.MANDT AND KUNNR = A.KUNNR AND KTOKD IN ( 'Z001', 'Z002', 'Z004' ) ) B ON A.KUNNR = B.KUNNR LEFT OUTER JOIN TABLE (SELECT DOCREQNO, ZSEQ, ZATTPATH, ZATTFN, ZATTFN_OR FROM SAPHEE.ZSDT1065 WHERE MANDT = A.MANDT AND DOCREQNO = A.QTNUM AND ZSEQ = A.QTSER AND ZWGBN = 'Q' -- 해외팀에서 첨부된 계약서 ) C ON C.DOCREQNO = A.QTNUM AND C.ZSEQ = A.QTSER LEFT OUTER JOIN TABLE ( SELECT QTNUM , QTSER , COUNT(*) AS PART_CNT FROM SAPHEE.ZCOBT309 WHERE MANDT = #{mandt} AND QTNUM = A.QTNUM AND QTSER = A.QTSER AND QTNUM = #{qtnum} AND QTSER = #{qtser} GROUP BY QTNUM, QTSER ) D ON A.QTNUM = D.QTNUM AND A.QTSER = D.QTSER LEFT OUTER JOIN TABLE ( SELECT QTNUM, QTSER, ZRTRSN FROM SAPHEE.ZSDT1057 AS TT WHERE MANDT =#{mandt} AND QTNUM = A.QTNUM AND QTSER = A.QTSER AND ZRQFLG = '1' AND ZRQSEQ = ( SELECT MAX(ZRQSEQ) FROM SAPHEE.ZSDT1057 WHERE MANDT = '183' AND QTNUM = TT.QTNUM AND QTSER = TT.QTSER) ) E ON A.QTNUM = E.QTNUM AND A.QTSER = E.QTSER LEFT JOIN TABLE ( SELECT USERNUMB AS USER_ID, USERNAME AS USER_NAME, USERTELE , USERFAXN, USERMBPN, USERMAIL FROM SAPHEE.ZUSERF U WHERE U.MANDT = #{mandt} AND USERNUMB = SUBSTR(A.ZKUNNR, 2,7) ) Z ON SUBSTR(A.ZKUNNR, 2,7) = Z.USER_ID LEFT JOIN SAPHEE.ZSDT1130 Y ON Y.MANDT = A.MANDT AND Y.DESTPORT = A.DESTPORT AND Y.LAND1 = B.LAND1 LEFT OUTER JOIN TABLE (SELECT ZSDT1154.MANDT, ZSDT1001.SONNR, ZSDT1001.SID, ZSDT1154.WWPER, ZSDT1154.WWSPD, ZSDT1154.ZREGN, ZSDT1154.WWBLD, ZSDT1154.WWCAP, ZSDT1154.WWUSE, ZSDT1154.WWFQ, ZSDT1154.PSTLZ1, ZSDT1154.PSTLZ2, ZSDT1154.ORT01_E, ZSDT1154.STRAS, ZSDT1154.MFLIFNRT, ZSDT1154.DMSTAT FROM SAPHEE.ZSDT1154 ZSDT1154, SAPHEE.ZSDT1001 ZSDT1001 WHERE ZSDT1154.MANDT = A.MANDT AND ZSDT1001.MANDT = A.MANDT AND ZSDT1001.SONNR = (SELECT MAX(SONNR) FROM SAPHEE.ZSDT1046 WHERE MANDT = A.MANDT AND QTNUM = A.QTNUM ) AND ZSDT1001.SID = ZSDT1154.SID ) H ON H.MANDT = A.MANDT ORDER BY A.QTNUM, A.QTSER WITH UR

